import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.blue[900],
      appBar: AppBar(
        backgroundColor: Colors.blue[900],
        elevation: 0,
        leading: Builder(
          builder: (context) => IconButton(
            icon: Icon(Icons.menu, color: Colors.amber[400]),
            onPressed: () => Scaffold.of(context).openDrawer(),
          ),
        ),
        title: Text('Analytics', style: TextStyle(color: Colors.amber[400], fontWeight: FontWeight.w600)),
      ),
      drawer: Drawer(
        child: ListView(
          padding: EdgeInsets.zero,
          children: [
            DrawerHeader(
              decoration: BoxDecoration(color: Colors.blue[800]),
              child: Text('Menu', style: TextStyle(color: Colors.amber[400], fontSize: 20)),
            ),
            ListTile(title: Text('Home', style: TextStyle(color: Colors.white)), leading: Icon(Icons.home, color: Colors.amber[400])),
            ListTile(title: Text('Settings', style: TextStyle(color: Colors.white)), leading: Icon(Icons.settings, color: Colors.amber[400])),
          ],
        ),
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text('Good Evening, Sophie!', style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold, color: Colors.white)),
            SizedBox(height: 20),
            GridView.count(
              crossAxisCount: 2,
              shrinkWrap: true,
              physics: NeverScrollableScrollPhysics(),
              crossAxisSpacing: 16,
              mainAxisSpacing: 16,
              children: [
                _buildMetricCard('10,123', 'Orders', Colors.amber[100]!, Icons.trending_up),
                _buildMetricCard('7,890', 'Users', Colors.amber[100]!, Icons.person_add),
                _buildMetricCard('93%', 'Satisfaction', Colors.amber[100]!, Icons.star),
                _buildMetricCard('\$50K', 'Revenue', Colors.amber[100]!, Icons.monetization_on),
              ],
            ),
            SizedBox(height: 24),
            Text('Recent Activity', style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600, color: Colors.amber[400])),
            SizedBox(height: 12),
            _buildActivityItem(Icons.shopping_cart, 'Order Placed', 'Order #9012', '2 min ago'),
            _buildActivityItem(Icons.person, 'User Joined', 'Sophie Brown joined', '5 min ago'),
            _buildActivityItem(Icons.payment, 'Payment', '\$2,499.99', '10 min ago'),
          ],
        ),
      ),
    );
  }

  Widget _buildMetricCard(String value, String label, Color bgColor, IconData icon) {
    return Container(
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: bgColor,
        borderRadius: BorderRadius.circular(4),
        boxShadow: [BoxShadow(color: Colors.black26, blurRadius: 8, offset: Offset(0, 2))],
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Row(
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: [
              Icon(icon, color: Colors.blue[900], size: 24),
              Icon(Icons.more_vert, color: Colors.grey[600], size: 16),
            ],
          ),
          Spacer(),
          Text(value, style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold, color: Colors.black87)),
          Text(label, style: TextStyle(fontSize: 12, color: Colors.grey[600])),
        ],
      ),
    );
  }

  Widget _buildActivityItem(IconData icon, String title, String subtitle, String time) {
    return Container(
      margin: EdgeInsets.only(bottom: 12),
      padding: EdgeInsets.all(12),
      decoration: BoxDecoration(
        color: Colors.blue[800],
        borderRadius: BorderRadius.circular(4),
        border: Border.all(color: Colors.amber[400]!),
      ),
      child: Row(
        children: [
          CircleAvatar(
            radius: 20,
            backgroundColor: Colors.amber[100],
            child: Icon(icon, color: Colors.blue[900], size: 20),
          ),
          SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(title, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 14, color: Colors.white)),
                Text(subtitle, style: TextStyle(color: Colors.grey[400], fontSize: 12)),
              ],
            ),
          ),
          Text(time, style: TextStyle(color: Colors.grey[400], fontSize: 11)),
        ],
      ),
    );
  }
}
