import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  List<Map<String, dynamic>> cartItems = [
    {'name': 'Laptop Stand', 'price': 29.99, 'quantity': 1, 'icon': Icons.laptop},
    {'name': 'Mouse Pad', 'price': 9.99, 'quantity': 2, 'icon': Icons.mouse},
  ];

  @override
  Widget build(BuildContext context) {
    double subtotal = cartItems.fold(0, (sum, item) => sum + (item['price'] * item['quantity']));
    double tax = subtotal * 0.08;
    double total = subtotal + tax;

    return Scaffold(
      appBar: AppBar(
        title: Text('Cart'),
        backgroundColor: Colors.white,
        foregroundColor: Colors.black,
        elevation: 0,
      ),
      backgroundColor: Colors.white,
      body: Column(
        children: [
          Expanded(
            child: ListView.builder(
              padding: EdgeInsets.symmetric(horizontal: 16, vertical: 8),
              itemCount: cartItems.length,
              itemBuilder: (context, index) {
                final item = cartItems[index];
                return Card(
                  margin: EdgeInsets.only(bottom: 8),
                  elevation: 1,
                  child: Padding(
                    padding: EdgeInsets.all(12),
                    child: Row(
                      children: [
                        Column(
                          children: [
                            IconButton(
                              onPressed: () {
                                setState(() {
                                  item['quantity']++;
                                });
                              },
                              icon: Icon(Icons.add_circle, color: Colors.red),
                            ),
                            Text('${item['quantity']}'),
                            IconButton(
                              onPressed: () {
                                setState(() {
                                  if (item['quantity'] > 1) item['quantity']--;
                                });
                              },
                              icon: Icon(Icons.remove_circle, color: Colors.red),
                            ),
                          ],
                        ),
                        Expanded(
                          child: Column(
                            crossAxisAlignment: CrossAxisAlignment.start,
                            children: [
                              Text(item['name'], style: TextStyle(fontSize: 16, fontWeight: FontWeight.w500)),
                              Text('\$${item['price'].toStringAsFixed(2)}', style: TextStyle(fontSize: 14, color: Colors.green)),
                            ],
                          ),
                        ),
                        Icon(item['icon'], size: 40, color: Colors.black54),
                      ],
                    ),
                  ),
                );
              },
            ),
          ),
          Container(
            padding: EdgeInsets.all(16),
            child: Column(
              children: [
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text('Subtotal:', style: TextStyle(fontSize: 16)),
                    Text('\$${subtotal.toStringAsFixed(2)}', style: TextStyle(fontSize: 16)),
                  ],
                ),
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text('Tax:', style: TextStyle(fontSize: 16)),
                    Text('\$${tax.toStringAsFixed(2)}', style: TextStyle(fontSize: 16)),
                  ],
                ),
                Divider(),
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text('Total:', style: TextStyle(fontSize: 18, fontWeight: FontWeight.w500)),
                    Text('\$${total.toStringAsFixed(2)}', style: TextStyle(fontSize: 18, fontWeight: FontWeight.w500)),
                  ],
                ),
                SizedBox(height: 16),
                SizedBox(
                  width: double.infinity,
                  child: ElevatedButton(
                    onPressed: () {},
                    style: ElevatedButton.styleFrom(
                      backgroundColor: Colors.red,
                      padding: EdgeInsets.symmetric(vertical: 16),
                      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
                      elevation: 2,
                    ),
                    child: Text('Checkout', style: TextStyle(fontSize: 16, color: Colors.white)),
                  ),
                ),
              ],
            ),
          ),
        ],
      ),
    );
  }
}
