import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return DefaultTabController(
      length: 4,
      child: Scaffold(
        backgroundColor: Color(0xFFF8F9FA),
        appBar: AppBar(
          backgroundColor: Color(0xFFF8F9FA),
          elevation: 0,
          title: Text(
            'Files',
            style: TextStyle(
              color: Colors.black87,
              fontSize: 28,
              fontWeight: FontWeight.w300,
              letterSpacing: -0.5,
            ),
          ),
          bottom: TabBar(
            indicatorColor: Colors.teal,
            labelColor: Colors.teal,
            unselectedLabelColor: Colors.grey[600],
            labelStyle: TextStyle(fontSize: 14, fontWeight: FontWeight.w500),
            tabs: [
              Tab(text: 'All'),
              Tab(text: 'Documents'),
              Tab(text: 'Media'),
              Tab(text: 'Recent'),
            ],
          ),
        ),
        body: TabBarView(
          children: [
            _buildFileList(),
            _buildFileList(),
            _buildFileList(),
            _buildFileList(),
          ],
        ),
        floatingActionButton: Container(
          width: 56,
          height: 56,
          child: FloatingActionButton(
            backgroundColor: Colors.teal,
            elevation: 2,
            child: Icon(Icons.add, size: 28),
            onPressed: () {},
          ),
        ),
      ),
    );
  }

  Widget _buildFileList() {
    return ListView(
      padding: EdgeInsets.symmetric(vertical: 16),
      children: [
        _buildMinimalFileItem('report.docx', '2.4 MB', Icons.description, Colors.blue),
        _buildDivider(),
        _buildMinimalFileItem('photo.jpg', '4.2 MB', Icons.image, Colors.green),
        _buildDivider(),
        _buildMinimalFileItem('song.mp3', '5.1 MB', Icons.music_note, Colors.orange),
        _buildDivider(),
        _buildMinimalFileItem('archive.zip', '12.8 MB', Icons.archive, Colors.grey[700]!),
        _buildDivider(),
        _buildMinimalFileItem('presentation.pptx', '8.3 MB', Icons.slideshow, Colors.red),
        _buildDivider(),
        _buildMinimalFileItem('spreadsheet.xlsx', '1.9 MB', Icons.table_chart, Colors.green),
        _buildDivider(),
        _buildMinimalFileItem('video.mp4', '45.6 MB', Icons.play_circle_fill, Colors.purple),
      ],
    );
  }

  Widget _buildMinimalFileItem(String name, String size, IconData icon, Color color) {
    return Padding(
      padding: EdgeInsets.symmetric(horizontal: 24, vertical: 12),
      child: Row(
        children: [
          Container(
            width: 32,
            height: 32,
            decoration: BoxDecoration(
              color: color.withOpacity(0.1),
              shape: BoxShape.circle,
            ),
            child: Icon(icon, color: color, size: 18),
          ),
          SizedBox(width: 16),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  name,
                  style: TextStyle(
                    fontSize: 16,
                    fontWeight: FontWeight.w400,
                    color: Colors.black87,
                    letterSpacing: -0.2,
                  ),
                ),
                SizedBox(height: 2),
                Text(
                  size,
                  style: TextStyle(
                    fontSize: 13,
                    color: Colors.grey[600],
                    fontWeight: FontWeight.w300,
                  ),
                ),
              ],
            ),
          ),
          Icon(
            Icons.more_horiz,
            color: Colors.grey[400],
            size: 20,
          ),
        ],
      ),
    );
  }

  Widget _buildDivider() {
    return Padding(
      padding: EdgeInsets.symmetric(horizontal: 72),
      child: Divider(
        height: 1,
        thickness: 0.5,
        color: Colors.grey[300],
      ),
    );
  }
}
