import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[100],
      appBar: AppBar(
        backgroundColor: Colors.grey[100],
        elevation: 0,
        leading: IconButton(
          icon: Icon(Icons.search, color: Colors.indigo),
          onPressed: () {},
        ),
        title: Text(
          'File Explorer',
          style: TextStyle(
            color: Colors.black,
            fontSize: 22,
            fontWeight: FontWeight.w400,
          ),
        ),
        actions: [
          IconButton(
            icon: Icon(Icons.sort, color: Colors.indigo),
            onPressed: () {},
          ),
        ],
      ),
      body: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Padding(
            padding: EdgeInsets.all(16),
            child: Wrap(
              spacing: 8,
              children: [
                _buildChip('Documents', Icons.description, Colors.indigo),
                _buildChip('Downloads', Icons.download, Colors.indigo),
                _buildChip('Music', Icons.music_note, Colors.indigo),
                _buildChip('Videos', Icons.video_library, Colors.indigo),
              ],
            ),
          ),
          Expanded(
            child: ListView(
              padding: EdgeInsets.all(16),
              children: [
                _buildListItem('Presentation.pptx', '4.5 MB', 'Today', Icons.description, Colors.indigo),
                _buildListItem('Images', '180 items', 'Yesterday', Icons.folder, Colors.indigo),
                _buildListItem('Videos', '2.3 GB', '2 days ago', Icons.folder, Colors.indigo),
                _buildListItem('Documents', '30 items', '1 week ago', Icons.folder, Colors.indigo),
              ],
            ),
          ),
        ],
      ),
      floatingActionButton: FloatingActionButton(
        backgroundColor: Colors.indigo,
        child: Icon(Icons.add),
        onPressed: () {},
      ),
      floatingActionButtonLocation: FloatingActionButtonLocation.centerFloat,
    );
  }

  Widget _buildChip(String label, IconData icon, Color color) {
    return Chip(
      avatar: Icon(icon, color: color, size: 20),
      label: Text(label, style: TextStyle(color: color)),
      backgroundColor: Colors.grey[100],
      side: BorderSide(color: color),
    );
  }

  Widget _buildListItem(String name, String size, String date, IconData icon, Color color) {
    return Container(
      margin: EdgeInsets.only(bottom: 16),
      padding: EdgeInsets.all(12),
      decoration: BoxDecoration(
        border: Border(bottom: BorderSide(color: color.withOpacity(0.3))),
      ),
      child: Row(
        children: [
          Icon(icon, color: color, size: 40),
          SizedBox(width: 16),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  name,
                  style: TextStyle(
                    fontSize: 16,
                    fontWeight: FontWeight.w500,
                    color: Colors.black87,
                  ),
                ),
                Text(
                  size,
                  style: TextStyle(fontSize: 12, color: Colors.grey[600]),
                ),
                Text(
                  date,
                  style: TextStyle(fontSize: 10, color: Colors.grey[500]),
                ),
              ],
            ),
          ),
        ],
      ),
    );
  }
}
