import 'package:flutter/material.dart';
import 'package:flutter_staggered_grid_view/flutter_staggered_grid_view.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      body: Container(
        decoration: BoxDecoration(
          gradient: LinearGradient(
            colors: [Colors.blue, Colors.purple],
            begin: Alignment.topLeft,
            end: Alignment.bottomRight,
          ),
        ),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            AppBar(
              backgroundColor: Colors.transparent,
              elevation: 0,
              title: Text(
                'Storage',
                style: TextStyle(
                  color: Colors.white,
                  fontSize: 26,
                  fontWeight: FontWeight.bold,
                ),
              ),
              actions: [
                IconButton(
                  icon: Icon(Icons.search, color: Colors.white),
                  onPressed: () {},
                ),
                IconButton(
                  icon: Icon(Icons.settings, color: Colors.white),
                  onPressed: () {},
                ),
              ],
            ),
            Padding(
              padding: EdgeInsets.all(16),
              child: Text(
                'Quick Access',
                style: TextStyle(
                  fontSize: 18,
                  fontWeight: FontWeight.w600,
                  color: Colors.white,
                ),
              ),
            ),
            Container(
              height: 100,
              child: ListView(
                scrollDirection: Axis.horizontal,
                padding: EdgeInsets.symmetric(horizontal: 16),
                children: [
                  _buildQuickAccessItem('Documents', Icons.description, Colors.red),
                  _buildQuickAccessItem('Downloads', Icons.download, Colors.orange),
                  _buildQuickAccessItem('Photos', Icons.image, Colors.yellow),
                  _buildQuickAccessItem('Videos', Icons.video_library, Colors.green),
                ],
              ),
            ),
            Expanded(
              child: StaggeredGridView.countBuilder(
                crossAxisCount: 4,
                itemCount: 4,
                padding: EdgeInsets.all(16),
                crossAxisSpacing: 16,
                mainAxisSpacing: 16,
                staggeredTileBuilder: (int index) => StaggeredTile.fit(2),
                itemBuilder: (context, index) {
                  List<Map<String, dynamic>> items = [
                    {'name': 'Invoice.pdf', 'size': '1.2 MB', 'date': 'Today', 'icon': Icons.picture_as_pdf, 'color': Colors.red},
                    {'name': 'Travel', 'size': '150 items', 'date': 'Yesterday', 'icon': Icons.folder, 'color': Colors.orange},
                    {'name': 'Music', 'size': '1.8 GB', 'date': '2 days ago', 'icon': Icons.folder, 'color': Colors.yellow},
                    {'name': 'Projects', 'size': '40 items', 'date': '1 week ago', 'icon': Icons.folder, 'color': Colors.green},
                  ];
                  return _buildFileItem(
                    items[index]['name'],
                    items[index]['size'],
                    items[index]['date'],
                    items[index]['icon'],
                    items[index]['color'],
                  );
                },
              ),
            ),
          ],
        ),
      ),
      floatingActionButton: FloatingActionButton(
        backgroundColor: Colors.purple,
        child: Icon(Icons.add),
        onPressed: () {},
      ),
    );
  }

  Widget _buildQuickAccessItem(String title, IconData icon, Color color) {
    return Container(
      width: 80,
      margin: EdgeInsets.only(right: 16),
      child: Column(
        children: [
          CircleAvatar(
            radius: 30,
            backgroundColor: color.withOpacity(0.3),
            child: Icon(icon, color: color, size: 30),
          ),
          SizedBox(height: 8),
          Text(
            title,
            style: TextStyle(fontSize: 12, color: Colors.white),
            textAlign: TextAlign.center,
          ),
        ],
      ),
    );
  }

  Widget _buildFileItem(String name, String size, String date, IconData icon, Color color) {
    return Container(
      decoration: BoxDecoration(
        gradient: LinearGradient(
          colors: [Colors.blue.withOpacity(0.3), Colors.purple.withOpacity(0.3)],
          begin: Alignment.topLeft,
          end: Alignment.bottomRight,
        ),
        borderRadius: BorderRadius.circular(12),
      ),
      child: Padding(
        padding: EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Icon(icon, color: color, size: 40),
            SizedBox(height: 12),
            Text(
              name,
              style: TextStyle(
                fontSize: 14,
                fontWeight: FontWeight.w600,
                color: Colors.white,
              ),
              maxLines: 2,
              overflow: TextOverflow.ellipsis,
            ),
            Spacer(),
            Text(
              size,
              style: TextStyle(fontSize: 12, color: Colors.white70),
            ),
            Text(
              date,
              style: TextStyle(fontSize: 10, color: Colors.white60),
            ),
          ],
        ),
      ),
    );
  }
}
