import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  List<Map<String, dynamic>> selectedFiles = [];

  void _addFile(String name, String size) {
    setState(() {
      selectedFiles.add({'name': name, 'size': size});
    });
  }

  void _removeFile(int index) {
    setState(() {
      selectedFiles.removeAt(index);
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFF121212),
      appBar: AppBar(
        title: Text('Image Upload', style: TextStyle(color: Colors.white)),
        backgroundColor: Color(0xFF121212),
        elevation: 0,
      ),
      body: Padding(
        padding: EdgeInsets.all(16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.stretch,
          children: [
            Container(
              height: 200,
              decoration: BoxDecoration(
                border: Border.all(color: Color(0xFF00BCD4), width: 2),
                borderRadius: BorderRadius.circular(12),
                color: Color(0xFF121212),
                boxShadow: [
                  BoxShadow(
                    color: Color(0xFF00BCD4).withOpacity(0.3),
                    blurRadius: 10,
                    spreadRadius: 2,
                  ),
                ],
              ),
              child: InkWell(
                onTap: () => _addFile('image_${selectedFiles.length + 1}.jpg', '3.0 MB'),
                borderRadius: BorderRadius.circular(12),
                child: Column(
                  mainAxisAlignment: MainAxisAlignment.center,
                  children: [
                    Icon(Icons.upload_file, size: 50, color: Color(0xFF00BCD4)),
                    SizedBox(height: 12),
                    Text(
                      'Upload Images Now',
                      style: TextStyle(fontSize: 16, fontWeight: FontWeight.w500, color: Colors.white),
                    ),
                    SizedBox(height: 8),
                    Text(
                      'PNG, JPG up to 8MB',
                      style: TextStyle(fontSize: 12, color: Colors.grey[400]),
                    ),
                  ],
                ),
              ),
            ),
            SizedBox(height: 20),
            ElevatedButton(
              onPressed: selectedFiles.isNotEmpty ? () {} : null,
              style: ElevatedButton.styleFrom(
                backgroundColor: Color(0xFF00BCD4),
                padding: EdgeInsets.symmetric(vertical: 14),
                shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(10)),
                elevation: 8,
                shadowColor: Color(0xFF00BCD4).withOpacity(0.5),
              ),
              child: Text(
                'Upload Now',
                style: TextStyle(fontSize: 16, fontWeight: FontWeight.w600, color: Colors.white),
              ),
            ),
            SizedBox(height: 24),
            if (selectedFiles.isNotEmpty)
              Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Text(
                    'Selected Files',
                    style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600, color: Colors.white),
                  ),
                  SizedBox(height: 12),
                  GridView.builder(
                    shrinkWrap: true,
                    physics: NeverScrollableScrollPhysics(),
                    gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                      crossAxisCount: 2,
                      crossAxisSpacing: 10,
                      mainAxisSpacing: 10,
                    ),
                    itemCount: selectedFiles.length,
                    itemBuilder: (context, index) {
                      return Container(
                        padding: EdgeInsets.all(8),
                        decoration: BoxDecoration(
                          border: Border.all(color: Color(0xFF00BCD4)),
                          borderRadius: BorderRadius.circular(8),
                          color: Color(0xFF121212),
                        ),
                        child: Column(
                          children: [
                            Expanded(
                              child: Container(
                                decoration: BoxDecoration(
                                  color: Colors.grey[800],
                                  borderRadius: BorderRadius.circular(4),
                                ),
                                child: Icon(Icons.image, color: Colors.grey[400]),
                              ),
                            ),
                            SizedBox(height: 8),
                            Text(
                              selectedFiles[index]['name'],
                              style: TextStyle(color: Colors.white, fontSize: 12),
                              overflow: TextOverflow.ellipsis,
                            ),
                            Text(
                              selectedFiles[index]['size'],
                              style: TextStyle(color: Colors.grey[400], fontSize: 10),
                            ),
                            IconButton(
                              onPressed: () => _removeFile(index),
                              icon: Icon(Icons.delete, color: Color(0xFF00BCD4)),
                            ),
                          ],
                        ),
                      );
                    },
                  ),
                ],
              ),
          ],
        ),
      ),
    );
  }
}
