import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  List<Map<String, dynamic>> cartItems = [
    {'name': 'Gaming Mouse', 'price': 89.99, 'quantity': 1, 'description': 'RGB gaming mouse', 'icon': Icons.mouse},
    {'name': 'Mechanical Keyboard', 'price': 159.99, 'quantity': 1, 'description': 'Cherry MX switches', 'icon': Icons.keyboard},
    {'name': 'Monitor Stand', 'price': 45.99, 'quantity': 2, 'description': 'Adjustable height', 'icon': Icons.desktop_windows}
  ];

  @override
  Widget build(BuildContext context) {
    double subtotal = cartItems.fold(0, (sum, item) => sum + (item['price'] * item['quantity']));
    double tax = subtotal * 0.08;
    double total = subtotal + tax;

    return Scaffold(
      appBar: AppBar(
        title: Text('My Cart'),
        backgroundColor: Colors.black,
        foregroundColor: Colors.white,
      ),
      backgroundColor: Colors.black,
      body: Column(
        children: [
          Expanded(
            child: ListView.builder(
              padding: EdgeInsets.all(16),
              itemCount: cartItems.length,
              itemBuilder: (context, index) {
                final item = cartItems[index];
                return Card(
                  margin: EdgeInsets.only(bottom: 16),
                  color: Colors.grey[900],
                  elevation: 8,
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(12)),
                  child: Padding(
                    padding: EdgeInsets.all(20),
                    child: Column(
                      children: [
                        Row(
                          children: [
                            Container(
                              padding: EdgeInsets.all(12),
                              decoration: BoxDecoration(
                                color: Colors.purple[700],
                                borderRadius: BorderRadius.circular(8),
                              ),
                              child: Icon(item['icon'], size: 32, color: Colors.white),
                            ),
                            SizedBox(width: 16),
                            Expanded(
                              child: Column(
                                crossAxisAlignment: CrossAxisAlignment.start,
                                children: [
                                  Text(item['name'], style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white)),
                                  Text(item['description'], style: TextStyle(color: Colors.grey[400])),
                                  Text('\$${item['price'].toStringAsFixed(2)}', style: TextStyle(fontSize: 16, color: Colors.purple[300])),
                                ],
                              ),
                            ),
                          ],
                        ),
                        SizedBox(height: 16),
                        Row(
                          mainAxisAlignment: MainAxisAlignment.spaceBetween,
                          children: [
                            Row(
                              children: [
                                IconButton(
                                  onPressed: () {
                                    setState(() {
                                      if (item['quantity'] > 1) item['quantity']--;
                                    });
                                  },
                                  icon: Icon(Icons.remove, color: Colors.purple[300]),
                                ),
                                Container(
                                  padding: EdgeInsets.symmetric(horizontal: 16, vertical: 8),
                                  decoration: BoxDecoration(
                                    border: Border.all(color: Colors.purple[300]!),
                                    borderRadius: BorderRadius.circular(4),
                                  ),
                                  child: Text('${item['quantity']}', style: TextStyle(color: Colors.white)),
                                ),
                                IconButton(
                                  onPressed: () {
                                    setState(() {
                                      item['quantity']++;
                                    });
                                  },
                                  icon: Icon(Icons.add, color: Colors.purple[300]),
                                ),
                              ],
                            ),
                            Text('\$${(item['price'] * item['quantity']).toStringAsFixed(2)}', 
                                 style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white)),
                          ],
                        ),
                      ],
                    ),
                  ),
                );
              },
            ),
          ),
          Container(
            padding: EdgeInsets.all(24),
            decoration: BoxDecoration(
              color: Colors.grey[900],
              borderRadius: BorderRadius.vertical(top: Radius.circular(20)),
            ),
            child: Column(
              children: [
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text('Subtotal:', style: TextStyle(fontSize: 16, color: Colors.white)),
                    Text('\$${subtotal.toStringAsFixed(2)}', style: TextStyle(fontSize: 16, color: Colors.white)),
                  ],
                ),
                SizedBox(height: 8),
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text('Tax:', style: TextStyle(fontSize: 16, color: Colors.white)),
                    Text('\$${tax.toStringAsFixed(2)}', style: TextStyle(fontSize: 16, color: Colors.white)),
                  ],
                ),
                Divider(color: Colors.grey[700]),
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text('Total:', style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold, color: Colors.purple[300])),
                    Text('\$${total.toStringAsFixed(2)}', style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold, color: Colors.purple[300])),
                  ],
                ),
              ],
            ),
          ),
        ],
      ),
      floatingActionButton: FloatingActionButton.extended(
        onPressed: () {},
        backgroundColor: Colors.purple[700],
        icon: Icon(Icons.shopping_cart_checkout, color: Colors.white),
        label: Text('Checkout', style: TextStyle(color: Colors.white)),
      ),
    );
  }
}
