import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[50],
      appBar: AppBar(
        backgroundColor: Colors.white,
        elevation: 1,
        iconTheme: IconThemeData(color: Colors.black),
        title: Text('User Management', style: TextStyle(color: Colors.black, fontWeight: FontWeight.w600)),
        actions: [
          Icon(Icons.more_vert, color: Colors.grey[600]),
          SizedBox(width: 16),
        ],
      ),
      drawer: Drawer(
        child: Column(
          children: [
            DrawerHeader(
              decoration: BoxDecoration(
                gradient: LinearGradient(
                  colors: [Colors.blue[600]!, Colors.blue[400]!],
                  begin: Alignment.topLeft,
                  end: Alignment.bottomRight,
                ),
              ),
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                mainAxisAlignment: MainAxisAlignment.end,
                children: [
                  CircleAvatar(
                    radius: 30,
                    backgroundImage: NetworkImage('https://via.placeholder.com/60'),
                  ),
                  SizedBox(height: 12),
                  Text('Admin Panel', style: TextStyle(color: Colors.white, fontSize: 18, fontWeight: FontWeight.bold)),
                ],
              ),
            ),
            _buildDrawerItem(Icons.dashboard, 'Dashboard', true),
            _buildDrawerItem(Icons.people, 'Users', false),
            _buildDrawerItem(Icons.shopping_cart, 'Orders', false),
            _buildDrawerItem(Icons.inventory, 'Products', false),
            _buildDrawerItem(Icons.analytics, 'Analytics', false),
            _buildDrawerItem(Icons.settings, 'Settings', false),
          ],
        ),
      ),
      body: Column(
        children: [
          Padding(
            padding: EdgeInsets.all(16),
            child: TextField(
              decoration: InputDecoration(
                hintText: 'Search users...',
                prefixIcon: Icon(Icons.search),
                suffixIcon: Icon(Icons.filter_list),
                filled: true,
                fillColor: Colors.white,
                border: OutlineInputBorder(
                  borderRadius: BorderRadius.circular(12),
                  borderSide: BorderSide.none,
                ),
              ),
            ),
          ),
          Expanded(
            child: ListView.builder(
              padding: EdgeInsets.symmetric(horizontal: 16),
              itemCount: 8,
              itemBuilder: (context, index) => _buildUserCard('John Doe ${index + 1}', 'john${index + 1}@email.com', index % 3 == 0 ? 'Admin' : index % 3 == 1 ? 'Manager' : 'User'),
            ),
          ),
        ],
      ),
      bottomSheet: Container(
        width: double.infinity,
        padding: EdgeInsets.all(16),
        child: ElevatedButton(
          onPressed: () {},
          style: ElevatedButton.styleFrom(
            backgroundColor: Colors.blue[600],
            padding: EdgeInsets.symmetric(vertical: 16),
            shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(12)),
          ),
          child: Text('Add New User', style: TextStyle(fontSize: 16, fontWeight: FontWeight.w600, color: Colors.white)),
        ),
      ),
    );
  }

  Widget _buildDrawerItem(IconData icon, String title, bool isSelected) {
    return ListTile(
      leading: Icon(icon, color: isSelected ? Colors.blue[600] : Colors.grey[600]),
      title: Text(title, style: TextStyle(color: isSelected ? Colors.blue[600] : Colors.grey[800], fontWeight: isSelected ? FontWeight.w600 : FontWeight.normal)),
      selected: isSelected,
      selectedTileColor: Colors.blue[50],
      onTap: () {},
    );
  }

  Widget _buildUserCard(String name, String email, String role) {
    return Card(
      margin: EdgeInsets.only(bottom: 12),
      elevation: 2,
      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(12)),
      child: Padding(
        padding: EdgeInsets.all(16),
        child: Row(
          children: [
            CircleAvatar(
              radius: 24,
              backgroundImage: NetworkImage('https://via.placeholder.com/48'),
            ),
            SizedBox(width: 12),
            Expanded(
              child: Column(
                crossAxisAlignment: CrossAxisAlignment.start,
                children: [
                  Text(name, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 16)),
                  Text(email, style: TextStyle(color: Colors.grey[600], fontSize: 14)),
                  SizedBox(height: 4),
                  Container(
                    padding: EdgeInsets.symmetric(horizontal: 8, vertical: 4),
                    decoration: BoxDecoration(
                      color: role == 'Admin' ? Colors.red[100] : role == 'Manager' ? Colors.orange[100] : Colors.blue[100],
                      borderRadius: BorderRadius.circular(12),
                    ),
                    child: Text(role, style: TextStyle(color: role == 'Admin' ? Colors.red[700] : role == 'Manager' ? Colors.orange[700] : Colors.blue[700], fontSize: 12, fontWeight: FontWeight.w500)),
                  ),
                ],
              ),
            ),
            Row(
              mainAxisSize: MainAxisSize.min,
              children: [
                IconButton(onPressed: () {}, icon: Icon(Icons.edit, color: Colors.blue[600], size: 20)),
                IconButton(onPressed: () {}, icon: Icon(Icons.delete, color: Colors.red[600], size: 20)),
              ],
            ),
          ],
        ),
      ),
    );
  }
}
