import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black,
      appBar: AppBar(
        backgroundColor: Colors.black,
        elevation: 0,
        title: Text('Command Center', style: TextStyle(fontFamily: 'Orbitron', color: Color(0xFF00FFFF), fontWeight: FontWeight.bold)),
        actions: [
          Icon(Icons.notifications, color: Color(0xFF00FFFF)),
          SizedBox(width: 16),
          CircleAvatar(
            backgroundImage: NetworkImage('https://via.placeholder.com/40'),
            radius: 18,
          ),
          SizedBox(width: 16),
        ],
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Row(
              children: [
                Expanded(child: _buildStatCard('Total Users', '2,134', Color(0xFF00FFFF))),
                SizedBox(width: 12),
                Expanded(child: _buildStatCard('Revenue', '67,430', Color(0xFF00FF00))),
                SizedBox(width: 12),
                Expanded(child: _buildStatCard('Orders', '1,056', Color(0xFFFF00FF))),
                SizedBox(width: 12),
                Expanded(child: _buildStatCard('Growth', '15.3%', Color(0xFF800080))),
              ],
            ),
            SizedBox(height: 24),
            Text('Recent Activities', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Color(0xFF00FFFF))),
            SizedBox(height: 16),
            ...List.generate(5, (index) => _buildActivityItem('User ${index + 1} performed action', '${index + 1}m ago')),
          ],
        ),
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {},
        child: Icon(Icons.add, color: Color(0xFF00FFFF)),
        backgroundColor: Colors.transparent,
        elevation: 0,
        highlightElevation: 0,
      ),
    );
  }

  Widget _buildStatCard(String title, String value, Color color) {
    return Container(
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: Colors.transparent,
        border: Border.all(color: color, width: 2),
        borderRadius: BorderRadius.circular(12),
        boxShadow: [BoxShadow(color: color.withOpacity(0.3), blurRadius: 8, spreadRadius: 2)],
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Text(title, style: TextStyle(color: Colors.white, fontSize: 12)),
          SizedBox(height: 8),
          Text(value, style: TextStyle(color: Colors.white, fontSize: 20, fontWeight: FontWeight.bold)),
        ],
      ),
    );
  }

  Widget _buildActivityItem(String activity, String time) {
    return Padding(
      padding: EdgeInsets.only(bottom: 12),
      child: Row(
        children: [
          Container(
            decoration: BoxDecoration(
              shape: BoxShape.circle,
              image: DecorationImage(
                image: NetworkImage('https://via.placeholder.com/40'),
                fit: BoxFit.cover,
              ),
              boxShadow: [BoxShadow(color: Color(0xFF00FFFF).withOpacity(0.5), blurRadius: 8, spreadRadius: 2)],
            ),
            width: 40,
            height: 40,
          ),
          SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(activity, style: TextStyle(fontWeight: FontWeight.w500, color: Colors.white)),
                Text(time, style: TextStyle(color: Color(0xFF00FFFF), fontSize: 12)),
              ],
            ),
          ),
        ],
      ),
    );
  }
}
