import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  List<Map<String, dynamic>> cartItems = [
    {'name': 'Luxury Watch', 'price': 499.99, 'quantity': 1, 'icon': Icons.watch},
    {'name': 'Gold Earphones', 'price': 199.99, 'quantity': 1, 'icon': Icons.earbuds},
  ];

  @override
  Widget build(BuildContext context) {
    double subtotal = cartItems.fold(0, (sum, item) => sum + (item['price'] * item['quantity']));
    double tax = subtotal * 0.08;
    double total = subtotal + tax;

    return Scaffold(
      appBar: AppBar(
        title: Text('Cart', style: TextStyle(fontFamily: 'Georgia')),
        backgroundColor: Colors.purple[900],
        foregroundColor: Colors.yellow[700],
      ),
      backgroundColor: Colors.purple[900],
      body: Column(
        children: [
          Expanded(
            child: ListView.builder(
              padding: EdgeInsets.all(20),
              itemCount: cartItems.length,
              itemBuilder: (context, index) {
                final item = cartItems[index];
                return Card(
                  margin: EdgeInsets.only(bottom: 16),
                  shape: RoundedRectangleBorder(
                    borderRadius: BorderRadius.circular(12),
                    side: BorderSide(color: Colors.yellow[700]!, width: 2),
                  ),
                  elevation: 4,
                  color: Colors.purple[800],
                  child: Padding(
                    padding: EdgeInsets.all(16),
                    child: Column(
                      children: [
                        Icon(item['icon'], size: 48, color: Colors.yellow[700]),
                        SizedBox(height: 8),
                        Text(item['name'], style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold, color: Colors.white, fontFamily: 'Georgia')),
                        Text('\$${item['price'].toStringAsFixed(2)}', style: TextStyle(fontSize: 14, color: Colors.greenAccent, fontFamily: 'Georgia')),
                        SizedBox(height: 8),
                        Row(
                          mainAxisAlignment: MainAxisAlignment.center,
                          children: [
                            IconButton(
                              onPressed: () {
                                setState(() {
                                  if (item['quantity'] > 1) item['quantity']--;
                                });
                              },
                              icon: Icon(Icons.remove_circle, color: Colors.yellow[700]),
                            ),
                            Text('${item['quantity']}', style: TextStyle(color: Colors.white, fontFamily: 'Georgia')),
                            IconButton(
                              onPressed: () {
                                setState(() {
                                  item['quantity']++;
                                });
                              },
                              icon: Icon(Icons.add_circle, color: Colors.yellow[700]),
                            ),
                          ],
                        ),
                      ],
                    ),
                  ),
                );
              },
            ),
          ),
          Container(
            padding: EdgeInsets.all(16),
            margin: EdgeInsets.all(16),
            decoration: BoxDecoration(
              border: Border.all(color: Colors.yellow[700]!, width: 2),
              color: Colors.purple[800],
            ),
            child: Column(
              children: [
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text('Subtotal:', style: TextStyle(fontSize: 16, color: Colors.white, fontFamily: 'Georgia')),
                    Text('\$${subtotal.toStringAsFixed(2)}', style: TextStyle(fontSize: 16, color: Colors.white, fontFamily: 'Georgia')),
                  ],
                ),
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text('Tax:', style: TextStyle(fontSize: 16, color: Colors.white, fontFamily: 'Georgia')),
                    Text('\$${tax.toStringAsFixed(2)}', style: TextStyle(fontSize: 16, color: Colors.white, fontFamily: 'Georgia')),
                  ],
                ),
                Divider(color: Colors.yellow[700]),
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceBetween,
                  children: [
                    Text('Total:', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white, fontFamily: 'Georgia')),
                    Text('\$${total.toStringAsFixed(2)}', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white, fontFamily: 'Georgia')),
                  ],
                ),
              ],
            ),
          ),
          Padding(
            padding: EdgeInsets.symmetric(horizontal: 16, vertical: 8),
            child: SizedBox(
              width: double.infinity,
              child: ElevatedButton(
                onPressed: () {},
                style: ElevatedButton.styleFrom(
                  backgroundColor: Colors.yellow[700],
                  padding: EdgeInsets.symmetric(vertical: 16),
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(12)),
                ),
                child: Text('Checkout', style: TextStyle(fontSize: 16, color: Colors.black, fontFamily: 'Georgia')),
              ),
            ),
          ),
        ],
      ),
    );
  }
}
