import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFF4B0082),
      body: SafeArea(
        child: Column(
          children: [
            Padding(
              padding: EdgeInsets.all(16),
              child: Row(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Icon(Icons.videogame_asset, color: Color(0xFFFFD700), size: 30),
                  SizedBox(width: 10),
                  Text(
                    'Leaderboard',
                    style: TextStyle(
                      fontFamily: 'Press Start 2P',
                      fontSize: 24,
                      color: Colors.white,
                    ),
                  ),
                ],
              ),
            ),
            Container(
              height: 160,
              padding: EdgeInsets.symmetric(horizontal: 16),
              child: Row(
                mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                children: [
                  _buildPodiumPlayer('John', '2,890', 1, Color(0xFFFFD700)),
                  _buildPodiumPlayer('Alice', '2,450', 2, Color(0xFFC0C0C0)),
                  _buildPodiumPlayer('Bob', '2,120', 3, Color(0xFFCD7F32)),
                ],
              ),
            ),
            SizedBox(height: 16),
            Expanded(
              child: ListView.builder(
                padding: EdgeInsets.all(16),
                itemCount: 7,
                itemBuilder: (context, index) {
                  List<Map<String, dynamic>> players = [
                    {'name': 'Emma', 'score': '1,980'},
                    {'name': 'Mike', 'score': '1,850'},
                    {'name': 'Sarah', 'score': '1,720'},
                    {'name': 'David', 'score': '1,650'},
                    {'name': 'Lisa', 'score': '1,580'},
                    {'name': 'Tom', 'score': '1,420'},
                    {'name': 'Anna', 'score': '1,350'},
                  ];
                  return _buildPlayerCard(players[index]['name'], players[index]['score'], index + 4);
                },
              ),
            ),
          ],
        ),
      ),
    );
  }

  Widget _buildPodiumPlayer(String name, String score, int rank, Color medalColor) {
    return Column(
      mainAxisAlignment: MainAxisAlignment.center,
      children: [
        Stack(
          alignment: Alignment.center,
          children: [
            Container(
              width: 40,
              height: 40,
              color: Colors.white,
              child: Icon(Icons.person, size: 30, color: Colors.grey[600]),
            ),
            Positioned(
              top: 0,
              right: 0,
              child: Container(
                padding: EdgeInsets.all(4),
                decoration: BoxDecoration(
                  color: medalColor,
                  shape: BoxShape.circle,
                ),
                child: Text(
                  rank.toString(),
                  style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold, fontSize: 12),
                ),
              ),
            ),
          ],
        ),
        SizedBox(height: 8),
        Text(name, style: TextStyle(fontFamily: 'Press Start 2P', color: Colors.white, fontSize: 12)),
        Text(score, style: TextStyle(fontFamily: 'Press Start 2P', color: Colors.white70, fontSize: 10)),
      ],
    );
  }

  Widget _buildPlayerCard(String name, String score, int rank) {
    return Container(
      margin: EdgeInsets.only(bottom: 12),
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: Colors.black,
        border: Border.all(color: Colors.white, width: 2),
        borderRadius: BorderRadius.circular(8),
      ),
      child: Row(
        children: [
          Container(
            width: 28,
            height: 28,
            color: Colors.grey[600],
            child: Center(
              child: Text(
                rank.toString(),
                style: TextStyle(fontFamily: 'Press Start 2P', color: Colors.white, fontSize: 10),
              ),
            ),
          ),
          SizedBox(width: 12),
          Container(
            width: 40,
            height: 40,
            color: Colors.blue[200],
            child: Icon(Icons.person, color: Colors.blue[800]),
          ),
          SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  name,
                  style: TextStyle(fontFamily: 'Press Start 2P', color: Colors.white, fontSize: 12),
                ),
                Text(
                  'Points',
                  style: TextStyle(fontFamily: 'Press Start 2P', color: Colors.white70, fontSize: 10),
                ),
              ],
            ),
          ),
          Text(
            score,
            style: TextStyle(fontFamily: 'Press Start 2P', color: Colors.blue[400], fontSize: 12),
          ),
        ],
      ),
    );
  }
}
