        ),
        child: Column(
          children: [
            AppBar(
              backgroundColor: Colors.transparent,
              elevation: 0,
              title: Text('Botany Terms', style: TextStyle(fontFamily: 'Caveat', color: Colors.brown[800], fontSize: 24)),
              actions: [
                IconButton(icon: Icon(Icons.settings, color: Colors.brown[800]), onPressed: () {}),
                IconButton(icon: Icon(Icons.menu, color: Colors.brown[800]), onPressed: () {}),
              ],
            ),
            Padding(
              padding: EdgeInsets.all(16),
              child: Column(
                children: [
                  Text('$currentCard of $totalCards', style: TextStyle(fontSize: 16, color: Colors.brown[800], fontFamily: 'Caveat')),
                  SizedBox(height: 8),
                  LinearProgressIndicator(
                    value: currentCard / totalCards,
                    backgroundColor: Colors.green[100],
                    valueColor: AlwaysStoppedAnimation<Color>(Colors.brown[800]!),
