import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  String pin = '';
  final int pinLength = 4;

  void _onNumberPressed(String number) {
    if (pin.length < pinLength) {
      setState(() {
        pin += number;
      });
    }
  }

  void _onDeletePressed() {
    if (pin.isNotEmpty) {
      setState(() {
        pin = pin.substring(0, pin.length - 1);
      });
    }
  }

  Widget _buildPinCircle(bool filled) {
    return Container(
      width: 50,
      height: 50,
      decoration: BoxDecoration(
        shape: BoxShape.circle,
        border: Border.all(color: Colors.purple[300]!, width: 2),
        color: filled ? Colors.white : Colors.transparent,
      ),
      child: filled ? Icon(Icons.circle, color: Colors.white, size: 18) : null,
    );
  }

  Widget _buildKeypadButton(String text, {VoidCallback? onPressed}) {
    return Container(
      width: 60,
      height: 60,
      child: ElevatedButton(
        onPressed: onPressed,
        style: ElevatedButton.styleFrom(
          backgroundColor: Colors.purple.withOpacity(0.3),
          foregroundColor: Colors.white,
          shape: CircleBorder(),
          elevation: 2,
        ),
        child: Text(text, style: TextStyle(fontSize: 20, fontWeight: FontWeight.w500)),
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Container(
        decoration: BoxDecoration(
          gradient: LinearGradient(
            colors: [Colors.deepPurple, Colors.indigo],
            begin: Alignment.topCenter,
            end: Alignment.bottomCenter,
          ),
        ),
        child: Padding(
          padding: EdgeInsets.all(15),
          child: Column(
            mainAxisAlignment: MainAxisAlignment.center,
            children: [
              Icon(Icons.lock_outline, size: 85, color: Colors.white),
              SizedBox(height: 25),
              Text('Unlock App', style: TextStyle(fontSize: 26, fontWeight: FontWeight.bold, color: Colors.white)),
              SizedBox(height: 12),
              Text('Enter 4-digit PIN', style: TextStyle(fontSize: 14, color: Colors.purple[100])),
              SizedBox(height: 25),
              Row(
                mainAxisAlignment: MainAxisAlignment.center,
                children: List.generate(pinLength, (index) => Padding(
                  padding: EdgeInsets.symmetric(horizontal: 8),
                  child: _buildPinCircle(index < pin.length),
                )),
              ),
              SizedBox(height: 25),
              GridView.builder(
                shrinkWrap: true,
                physics: NeverScrollableScrollPhysics(),
                gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                  crossAxisCount: 3,
                  crossAxisSpacing: 15,
                  mainAxisSpacing: 15,
                  childAspectRatio: 1,
                ),
                itemCount: 12,
                itemBuilder: (context, index) {
                  if (index < 9) {
                    String number = (index + 1).toString();
                    return _buildKeypadButton(number, onPressed: () => _onNumberPressed(number));
                  } else if (index == 9) {
                    return Container();
                  } else if (index == 10) {
                    return _buildKeypadButton('0', onPressed: () => _onNumberPressed('0'));
                  } else {
                    return _buildKeypadButton('âŒ«', onPressed: _onDeletePressed);
                  }
                },
              ),
            ],
          ),
        ),
      ),
    );
  }
}
