import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  List<Map<String, dynamic>> wishlistItems = [
    {'name': 'Wireless Headphones', 'price': '99.99', 'icon': Icons.headphones},
    {'name': 'Smartphone', 'price': '699.99', 'icon': Icons.phone_android},
    {'name': 'Laptop', 'price': '1299.99', 'icon': Icons.laptop},
    {'name': 'Watch', 'price': '299.99', 'icon': Icons.watch},
  ];

  void removeItem(int index) {
    setState(() {
      wishlistItems.removeAt(index);
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFFFF8E1),
      appBar: AppBar(
        backgroundColor: Color(0xFFDD2C00),
        title: Row(
          children: [
            Icon(Icons.favorite, color: Colors.red[900], size: 24),
            SizedBox(width: 8),
            Text('Wishlist', style: TextStyle(color: Colors.black, fontSize: 20, fontFamily: 'PressStart2P')),
          ],
        ),
      ),
      body: wishlistItems.isEmpty
          ? Center(
              child: Column(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Icon(Icons.favorite_border, size: 80, color: Colors.grey[400]),
                  SizedBox(height: 16),
                  Text('Your wishlist is empty', style: TextStyle(fontSize: 16, color: Colors.grey[600], fontFamily: 'PressStart2P')),
                ],
              ),
            )
          : ListView.builder(
              padding: EdgeInsets.all(16),
              itemCount: wishlistItems.length,
              itemBuilder: (context, index) {
                final item = wishlistItems[index];
                return Container(
                  margin: EdgeInsets.only(bottom: 12),
                  decoration: BoxDecoration(
                    color: Colors.white,
                    border: Border.all(color: Colors.black, width: 2),
                    borderRadius: BorderRadius.circular(0),
                  ),
                  child: ListTile(
                    contentPadding: EdgeInsets.all(16),
                    leading: Container(
                      width: 50,
                      height: 50,
                      color: Colors.green[100],
                      child: Icon(item['icon'], size: 28, color: Colors.green[800]),
                    ),
                    title: Text(
                      item['name'],
                      style: TextStyle(fontSize: 14, fontFamily: 'PressStart2P'),
                    ),
                    subtitle: Text(
                      item['price'],
                      style: TextStyle(fontSize: 12, color: Colors.green[600], fontFamily: 'PressStart2P'),
                    ),
                    trailing: IconButton(
                      icon: Icon(Icons.favorite, color: Colors.red[900], size: 24),
                      onPressed: () => removeItem(index),
                    ),
                  ),
                );
              },
            ),
      floatingActionButton: FloatingActionButton(
        backgroundColor: Colors.green[800],
        child: Icon(Icons.add, color: Colors.white),
        onPressed: () {},
      ),
    );
  }
}
