import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  List<Map<String, dynamic>> wishlistItems = [
    {'name': 'Wireless Headphones', 'price': '99.99', 'icon': Icons.headphones},
    {'name': 'Smartphone', 'price': '699.99', 'icon': Icons.phone_android},
    {'name': 'Laptop', 'price': '1299.99', 'icon': Icons.laptop},
    {'name': 'Watch', 'price': '299.99', 'icon': Icons.watch},
  ];

  void removeItem(int index) {
    setState(() {
      wishlistItems.removeAt(index);
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFB8860B),
      appBar: AppBar(
        backgroundColor: Colors.black,
        title: Row(
          children: [
            Icon(Icons.favorite, color: Colors.amber[400], size: 24),
            SizedBox(width: 8),
            Text('Wishlist', style: TextStyle(color: Colors.amber[400], fontSize: 20, fontWeight: FontWeight.w600, fontFamily: 'PlayfairDisplay')),
          ],
        ),
      ),
      body: wishlistItems.isEmpty
          ? Center(
              child: Column(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Icon(Icons.favorite_border, size: 80, color: Colors.amber[200]),
                  SizedBox(height: 16),
                  Text('Your wishlist is empty', style: TextStyle(fontSize: 18, color: Colors.amber[200], fontFamily: 'PlayfairDisplay')),
                ],
              ),
            )
          : ListView.builder(
              padding: EdgeInsets.all(16),
              itemCount: wishlistItems.length,
              itemBuilder: (context, index) {
                final item = wishlistItems[index];
                return Container(
                  margin: EdgeInsets.only(bottom: 12),
                  decoration: BoxDecoration(
                    color: Colors.black,
                    border: Border.all(color: Colors.amber[400], width: 1),
                    borderRadius: BorderRadius.circular(12),
                  ),
                  child: ListTile(
                    contentPadding: EdgeInsets.all(16),
                    leading: Container(
                      width: 60,
                      height: 60,
                      decoration: BoxDecoration(
                        color: Colors.amber[50],
                        borderRadius: BorderRadius.circular(8),
                      ),
                      child: Icon(item['icon'], size: 30, color: Colors.amber[400]),
                    ),
                    title: Text(
                      item['name'],
                      style: TextStyle(fontSize: 16, fontWeight: FontWeight.w500, color: Colors.amber[400], fontFamily: 'PlayfairDisplay'),
                    ),
                    subtitle: Text(
                      item['price'],
                      style: TextStyle(fontSize: 14, color: Colors.amber[200], fontWeight: FontWeight.w600),
                    ),
                    trailing: IconButton(
                      icon: Icon(Icons.favorite, color: Colors.amber[400], size: 24),
                      onPressed: () => removeItem(index),
                    ),
                  ),
                );
              },
            ),
      floatingActionButton: FloatingActionButton(
        backgroundColor: Colors.black,
        child: Icon(Icons.add, color: Colors.amber[400]),
        onPressed: () {},
      ),
    );
  }
}
