import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black,
      appBar: AppBar(
        backgroundColor: Colors.black,
        elevation: 0,
        title: Text(
          'Categories',
          style: TextStyle(
            color: Colors.white,
            fontSize: 24,
            fontWeight: FontWeight.bold,
            fontFamily: 'RobotoMono',
          ),
        ),
        centerTitle: true,
      ),
      body: Padding(
        padding: EdgeInsets.all(8.0),
        child: GridView.count(
          crossAxisCount: 3,
          crossAxisSpacing: 8,
          mainAxisSpacing: 8,
          childAspectRatio: 1.0,
          children: [
            _buildCategoryCard(Icons.shopping_bag, 'Electronics', Colors.greenAccent),
            _buildCategoryCard(Icons.headphones, 'Audio', Colors.greenAccent),
            _buildCategoryCard(Icons.laptop, 'Computers', Colors.greenAccent),
            _buildCategoryCard(Icons.checkroom, 'Fashion', Colors.greenAccent),
            _buildCategoryCard(Icons.book, 'Books', Colors.greenAccent),
            _buildCategoryCard(Icons.home, 'Home', Colors.greenAccent),
          ],
        ),
      ),
    );
  }

  Widget _buildCategoryCard(IconData icon, String title, Color iconColor) {
    return Container(
      decoration: BoxDecoration(
        color: Colors.grey[900],
        shape: BoxShape.circle,
        boxShadow: [
          BoxShadow(
            color: Colors.greenAccent.withOpacity(0.3),
            blurRadius: 8,
            spreadRadius: 2,
          ),
        ],
      ),
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          Icon(
            icon,
            size: 36,
            color: iconColor,
          ),
          SizedBox(height: 8),
          Text(
            title,
            style: TextStyle(
              fontSize: 14,
              fontWeight: FontWeight.w500,
              color: Colors.white,
              fontFamily: 'RobotoMono',
            ),
          ),
        ],
      ),
    );
  }
}
