import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool isRunning = false;
  int seconds = 0;
  String currentTask = 'Retro Task';
  TextEditingController taskController = TextEditingController();

  String formatTime(int totalSeconds) {
    int hours = totalSeconds ~/ 3600;
    int minutes = (totalSeconds % 3600) ~/ 60;
    int secs = totalSeconds % 60;
    return '${hours.toString().padLeft(2, '0')}:${minutes.toString().padLeft(2, '0')}:${secs.toString().padLeft(2, '0')}';
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.green[900],
      appBar: AppBar(
        title: Text('Time Tracker', style: TextStyle(fontFamily: 'PressStart2P')), 
        backgroundColor: Colors.black,
        foregroundColor: Colors.white,
        elevation: 0,
      ),
      body: Padding(
        padding: EdgeInsets.all(20),
        child: Column(
          children: [
            Container(
              padding: EdgeInsets.all(16),
              decoration: BoxDecoration(
                color: Colors.black,
                border: Border.all(color: Colors.white, width: 4),
              ),
              child: Text(
                formatTime(seconds),
                style: TextStyle(
                  fontSize: 36,
                  fontFamily: 'PressStart2P',
                  color: Colors.white,
                ),
              ),
            ),
            SizedBox(height: 20),
            ElevatedButton(
              onPressed: () => setState(() => isRunning = !isRunning),
              style: ElevatedButton.styleFrom(
                backgroundColor: isRunning ? Colors.red[600] : Colors.green[600],
                shape: RoundedRectangleBorder(borderRadius: BorderRadius.zero),
                padding: EdgeInsets.all(16),
                side: BorderSide(color: Colors.white, width: 2),
              ),
              child: Text(
                isRunning ? 'PAUSE' : 'START',
                style: TextStyle(fontSize: 16, fontFamily: 'PressStart2P', color: Colors.white),
              ),
            ),
            SizedBox(height: 20),
            TextField(
              controller: taskController,
              decoration: InputDecoration(
                hintText: 'Task name',
                filled: true,
                fillColor: Colors.black,
                hintStyle: TextStyle(color: Colors.white70, fontFamily: 'PressStart2P'),
                border: OutlineInputBorder(borderRadius: BorderRadius.zero, borderSide: BorderSide(color: Colors.white, width: 2)),
              ),
              style: TextStyle(color: Colors.white, fontFamily: 'PressStart2P'),
            ),
            SizedBox(height: 16),
            if (isRunning)
              Container(
                padding: EdgeInsets.all(12),
                decoration: BoxDecoration(
                  color: Colors.black,
                  border: Border.all(color: Colors.white, width: 2),
                ),
                child: Row(
                  children: [
                    Expanded(
                      child: Column(
                        crossAxisAlignment: CrossAxisAlignment.start,
                        children: [
                          Text(currentTask, style: TextStyle(fontSize: 14, color: Colors.white, fontFamily: 'PressStart2P')),
                          Text('${formatTime(seconds)} elapsed', style: TextStyle(color: Colors.white70, fontFamily: 'PressStart2P')),
                        ],
                      ),
                    ),
                    IconButton(
                      onPressed: () => setState(() => isRunning = false),
                      icon: Icon(Icons.stop, color: Colors.red[600]),
                    ),
                  ],
                ),
              ),
            SizedBox(height: 16),
            Expanded(
              child: ListView.separated(
                itemCount: 3,
                separatorBuilder: (context, index) => SizedBox(height: 8),
                itemBuilder: (context, index) => Container(
                  color: Colors.black,
                  child: ListTile(
                    title: Text('Task ${index + 1}', style: TextStyle(color: Colors.white, fontFamily: 'PressStart2P')),
                    subtitle: Text('1h ${index * 15}m â€¢ Today', style: TextStyle(color: Colors.white70, fontFamily: 'PressStart2P')),
                  ),
                ),
              ),
            ),
          ],
        ),
      ),
    );
  }
}
