              children: [
                Text('Current Balance', style: TextStyle(fontSize: 18, color: Colors.white70)),
                SizedBox(height: 8),
                Text('2,450.00', style: TextStyle(fontSize: 36, fontWeight: FontWeight.bold, color: Colors.white)),
              ],
            ),
          ),
          Container(
            height: 120,
            margin: EdgeInsets.symmetric(vertical: 8),
            child: ListView(
              scrollDirection: Axis.horizontal,
