import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.grey[100],
      appBar: AppBar(
        title: Text('Expense Tracker', style: TextStyle(color: Colors.black87, fontWeight: FontWeight.w600)),
        backgroundColor: Colors.grey[100],
        elevation: 0,
        centerTitle: true,
      ),
      body: Column(
        children: [
          Container(
            margin: EdgeInsets.symmetric(horizontal: 12, vertical: 8),
            padding: EdgeInsets.symmetric(vertical: 16, horizontal: 20),
            decoration: BoxDecoration(
              color: Colors.indigo[600],
              borderRadius: BorderRadius.circular(8),
            ),
            child: Column(
              children: [
                Text('Current Balance', style: TextStyle(fontSize: 14, color: Colors.white70)),
                SizedBox(height: 4),
                Text('2,450.00', style: TextStyle(fontSize: 28, fontWeight: FontWeight.w600, color: Colors.white)),
              ],
            ),
          ),
          Container(
            height: 150,
            padding: EdgeInsets.symmetric(horizontal: 12, vertical: 8),
            child: GridView.count(
              crossAxisCount: 2,
              crossAxisSpacing: 8,
              mainAxisSpacing: 8,
              shrinkWrap: true,
              children: [
                _buildCategoryCard('Food', Icons.restaurant, Colors.grey[600]!),
                _buildCategoryCard('Transport', Icons.directions_car, Colors.indigo[400]!),
                _buildCategoryCard('Shopping', Icons.shopping_bag, Colors.green[400]!),
                _buildCategoryCard('Bills', Icons.receipt, Colors.coral[400]!),
              ],
            ),
          ),
          Expanded(
            child: ListView.builder(
              padding: EdgeInsets.symmetric(horizontal: 12, vertical: 8),
              itemCount: 4,
              itemBuilder: (context, index) {
                final transactions = [
                  {'title': 'Grocery Store', 'amount': '-45.20', 'icon': Icons.restaurant, 'color': Colors.grey[600]},
                  {'title': 'Gas Station', 'amount': '-32.50', 'icon': Icons.directions_car, 'color': Colors.indigo[400]},
                  {'title': 'Online Shopping', 'amount': '-89.99', 'icon': Icons.shopping_bag, 'color': Colors.green[400]},
                  {'title': 'Electricity Bill', 'amount': '-125.00', 'icon': Icons.receipt, 'color': Colors.coral[400]},
                ];
                return _buildTransactionItem(
                  transactions[index]['title'] as String,
                  transactions[index]['amount'] as String,
                  transactions[index]['icon'] as IconData,
                  transactions[index]['color'] as Color,
                );
              },
            ),
          ),
        ],
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {},
        child: Icon(Icons.add, color: Colors.white),
        backgroundColor: Colors.coral[400],
        shape: CircleBorder(),
      ),
    );
  }

  Widget _buildCategoryCard(String title, IconData icon, Color color) {
    return Container(
      decoration: BoxDecoration(
        color: color.withOpacity(0.2),
        borderRadius: BorderRadius.circular(8),
      ),
      child: Column(
        mainAxisAlignment: MainAxisAlignment.center,
        children: [
          Icon(icon, color: color, size: 24),
          SizedBox(height: 4),
          Text(title, style: TextStyle(fontWeight: FontWeight.w500, color: color, fontSize: 12)),
        ],
      ),
    );
  }

  Widget _buildTransactionItem(String title, String amount, IconData icon, Color color) {
    return Padding(
      padding: EdgeInsets.symmetric(vertical: 4),
      child: Row(
        children: [
          Icon(icon, color: color, size: 18),
          SizedBox(width: 8),
          Expanded(child: Text(title, style: TextStyle(fontWeight: FontWeight.w500, fontSize: 14))),
          Text(amount, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 14, color: Colors.red[600])),
        ],
      ),
    );
  }
}
