import 'package:flutter/material.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      debugShowCheckedModeBanner: false,
      home: GeneratedWidget(),
    );
  }
}

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  int _selectedIndex = 0;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFF0A0A0A),
      appBar: AppBar(
        backgroundColor: Color(0xFF1A1A1A),
        elevation: 0,
        leading: Icon(Icons.menu, color: Colors.white),
        title: Text('Dashboard', style: TextStyle(color: Colors.white, fontWeight: FontWeight.w600)),
        actions: [
          Icon(Icons.search, color: Colors.cyan),
          SizedBox(width: 16),
          Icon(Icons.notifications_outlined, color: Colors.white),
          SizedBox(width: 16),
        ],
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text('Good Evening, John!', style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold, color: Colors.white)),
            SizedBox(height: 20),
            GridView.count(
              crossAxisCount: 2,
              shrinkWrap: true,
              physics: NeverScrollableScrollPhysics(),
              crossAxisSpacing: 16,
              mainAxisSpacing: 16,
              children: [
                _buildNeonCard('1,234', 'Total Sales', Colors.cyan, Icons.trending_up),
                _buildNeonCard('567', 'New Users', Colors.lime, Icons.person_add),
                _buildNeonCard('89%', 'Conversion', Colors.pinkAccent, Icons.analytics),
                _buildNeonCard('$12.5K', 'Revenue', Colors.yellow, Icons.monetization_on),
              ],
            ),
            SizedBox(height: 24),
            Text('Recent Activity', style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600, color: Colors.white)),
            SizedBox(height: 12),
            _buildActivityItem(Icons.shopping_cart, 'New Order', 'Order #1234 received', '2 min ago'),
            _buildActivityItem(Icons.person, 'User Registration', 'John Doe joined', '5 min ago'),
            _buildActivityItem(Icons.payment, 'Payment Received', '$299.99 from client', '10 min ago'),
            SizedBox(height: 80),
          ],
        ),
      ),
      bottomNavigationBar: BottomNavigationBar(
        currentIndex: _selectedIndex,
        onTap: (int index) {
          setState(() {
            _selectedIndex = index;
          });
          if (index == 1) _showOrdersDialog();
          else if (index == 2) _showSettingsDialog();
        },
        backgroundColor: Color(0xFF1A1A1A),
        selectedItemColor: Colors.cyan,
        unselectedItemColor: Colors.grey[600],
        type: BottomNavigationBarType.fixed,
        items: [
          BottomNavigationBarItem(icon: Icon(Icons.home), label: 'Home'),
          BottomNavigationBarItem(icon: Icon(Icons.shopping_cart), label: 'Orders'),
          BottomNavigationBarItem(icon: Icon(Icons.settings), label: 'Settings'),
        ],
      ),
    );
  }

  Widget _buildNeonCard(String value, String label, Color neonColor, IconData icon) {
    return Container(
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: Color(0xFF1A1A1A),
        borderRadius: BorderRadius.circular(12),
        border: Border.all(color: neonColor.withOpacity(0.5), width: 1),
        boxShadow: [BoxShadow(color: neonColor.withOpacity(0.2), blurRadius: 8)],
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Row(
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: [
              Icon(icon, color: neonColor, size: 24),
              Container(width: 6, height: 6, decoration: BoxDecoration(color: neonColor, shape: BoxShape.circle, boxShadow: [BoxShadow(color: neonColor, blurRadius: 4, spreadRadius: 1)])),
            ],
          ),
          Spacer(),
          Text(value, style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold, color: Colors.white)),
          Text(label, style: TextStyle(fontSize: 12, color: Colors.grey[400])),
        ],
      ),
    );
  }

  Widget _buildActivityItem(IconData icon, String title, String subtitle, String time) {
    return Container(
      margin: EdgeInsets.only(bottom: 12),
      padding: EdgeInsets.all(12),
      decoration: BoxDecoration(color: Color(0xFF1A1A1A), borderRadius: BorderRadius.circular(8)),
      child: Row(
        children: [
          CircleAvatar(radius: 20, backgroundColor: Colors.cyan.withOpacity(0.1), child: Icon(icon, color: Colors.cyan, size: 20)),
          SizedBox(width: 12),
          Expanded(child: Column(crossAxisAlignment: CrossAxisAlignment.start, children: [Text(title, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 14, color: Colors.white)), Text(subtitle, style: TextStyle(color: Colors.grey[400], fontSize: 12))])),
          Text(time, style: TextStyle(color: Colors.grey[500], fontSize: 11)),
        ],
      ),
    );
  }

  void _showOrdersDialog() {
    showDialog(context: context, builder: (BuildContext context) {
      return AlertDialog(
        backgroundColor: Color(0xFF1A1A1A),
        title: Row(children: [Icon(Icons.shopping_cart, color: Colors.green), SizedBox(width: 8), Text('Recent Orders', style: TextStyle(color: Colors.white))]),
        content: Container(width: double.maxFinite, child: Column(mainAxisSize: MainAxisSize.min, children: [
          _buildOrderItem('#1234', 'Electronics Package', '$299.99', 'Delivered', Colors.green),
          _buildOrderItem('#1233', 'Office Supplies', '$89.50', 'Shipped', Colors.cyan),
          _buildOrderItem('#1232', 'Books Collection', '$45.99', 'Processing', Colors.yellow),
          _buildOrderItem('#1231', 'Home Decor', '$159.99', 'Cancelled', Colors.red),
        ])),
        actions: [
          TextButton(onPressed: () => Navigator.pop(context), child: Text('View All Orders', style: TextStyle(color: Colors.cyan))),
          TextButton(onPressed: () => Navigator.pop(context), child: Text('Close', style: TextStyle(color: Colors.cyan))),
        ],
      );
    });
  }

  Widget _buildOrderItem(String orderNumber, String itemName, String amount, String status, Color statusColor) {
    return Container(
      margin: EdgeInsets.only(bottom: 12),
      padding: EdgeInsets.all(12),
      decoration: BoxDecoration(color: Color(0xFF1A1A1A), borderRadius: BorderRadius.circular(8), border: Border.all(color: Color(0xFF2A2A2A))),
      child: Row(children: [
        Icon(Icons.shopping_bag, color: Colors.grey[400], size: 20),
        SizedBox(width: 12),
        Expanded(child: Column(crossAxisAlignment: CrossAxisAlignment.start, children: [
          Text('Order $orderNumber', style: TextStyle(fontWeight: FontWeight.w600, fontSize: 14, color: Colors.white)),
          Text(itemName, style: TextStyle(color: Colors.grey[400], fontSize: 12)),
          Text(amount, style: TextStyle(fontWeight: FontWeight.bold, fontSize: 13, color: Colors.white)),
        ])),
        Container(padding: EdgeInsets.symmetric(horizontal: 8, vertical: 4), decoration: BoxDecoration(color: statusColor.withOpacity(0.1), borderRadius: BorderRadius.circular(12)), child: Text(status, style: TextStyle(color: statusColor, fontSize: 11, fontWeight: FontWeight.w500))),
      ]),
    );
  }

  void _showSettingsDialog() {
    showDialog(context: context, builder: (BuildContext context) {
      return AlertDialog(
        backgroundColor: Color(0xFF1A1A1A),
        title: Row(children: [Icon(Icons.settings, color: Colors.cyan), SizedBox(width: 8), Text('Settings', style: TextStyle(color: Colors.white))]),
        content: Column(mainAxisSize: MainAxisSize.min, children: [
          ListTile(leading: Icon(Icons.person, color: Colors.grey[400]), title: Text('Profile', style: TextStyle(color: Colors.white)), trailing: Icon(Icons.arrow_forward_ios, size: 16, color: Colors.grey[400])),
          ListTile(leading: Icon(Icons.notifications, color: Colors.grey[400]), title: Text('Notifications', style: TextStyle(color: Colors.white)), trailing: Icon(Icons.arrow_forward_ios, size: 16, color: Colors.grey[400])),
          ListTile(leading: Icon(Icons.security, color: Colors.grey[400]), title: Text('Privacy & Security', style: TextStyle(color: Colors.white)), trailing: Icon(Icons.arrow_forward_ios, size: 16, color: Colors.grey[400])),
          ListTile(leading: Icon(Icons.help, color: Colors.grey[400]), title: Text('Help & Support', style: TextStyle(color: Colors.white)), trailing: Icon(Icons.arrow_forward_ios, size: 16, color: Colors.grey[400])),
        ]),
        actions: [TextButton(onPressed: () => Navigator.pop(context), child: Text('Close', style: TextStyle(color: Colors.cyan)))],
      );
    });
  }
}
