import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      appBar: AppBar(
        backgroundColor: Colors.white,
        elevation: 0,
        leading: Icon(Icons.menu, color: Colors.green[800]),
        title: Text('Control Panel', style: TextStyle(color: Colors.green[800], fontWeight: FontWeight.w600)),
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(10),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text('Hi, Olivia!', style: TextStyle(fontSize: 20, fontWeight: FontWeight.w600, color: Colors.black87)),
            SizedBox(height: 12),
            Column(
              children: [
                _buildMetricCard('8,901', 'Orders', Colors.green[100]!, Icons.trending_up),
                SizedBox(height: 8),
                _buildMetricCard('5,678', 'Users', Colors.green[100]!, Icons.person_add),
                SizedBox(height: 8),
                _buildMetricCard('89%', 'Rating', Colors.green[100]!, Icons.star),
                SizedBox(height: 8),
                _buildMetricCard('40K', 'Revenue', Colors.green[100]!, Icons.monetization_on),
              ],
            ),
            SizedBox(height: 16),
            Text('Recent Actions', style: TextStyle(fontSize: 16, fontWeight: FontWeight.w600, color: Colors.green[800])),
            SizedBox(height: 8),
            _buildActivityItem(Icons.shopping_cart, 'Order Processed', 'Order #6789', '1 min ago'),
            _buildActivityItem(Icons.person, 'User Added', 'Olivia Green joined', '3 min ago'),
            _buildActivityItem(Icons.payment, 'Payment', '1,499.99', '7 min ago'),
          ],
        ),
      ),
    );
  }

  Widget _buildMetricCard(String value, String label, Color bgColor, IconData icon) {
    return Container(
      padding: EdgeInsets.all(10),
      decoration: BoxDecoration(
        color: bgColor,
        borderRadius: BorderRadius.circular(8),
        boxShadow: [BoxShadow(color: Colors.grey[300]!, blurRadius: 4, offset: Offset(0, 1))],
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Row(
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: [
              Icon(icon, color: Colors.green[800], size: 20),
              Icon(Icons.more_vert, color: Colors.grey[600], size: 12),
            ],
          ),
          SizedBox(height: 6),
          Text(value, style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold, color: Colors.black87)),
          Text(label, style: TextStyle(fontSize: 10, color: Colors.grey[600])),
        ],
      ),
    );
  }

  Widget _buildActivityItem(IconData icon, String title, String subtitle, String time) {
    return Container(
      margin: EdgeInsets.only(bottom: 8),
      padding: EdgeInsets.all(8),
      decoration: BoxDecoration(
        color: Colors.white,
        borderRadius: BorderRadius.circular(6),
        border: Border.all(color: Colors.green[200]!),
      ),
      child: Row(
        children: [
          CircleAvatar(
            radius: 16,
            backgroundColor: Colors.green[100],
            child: Icon(icon, color: Colors.green[800], size: 16),
          ),
          SizedBox(width: 8),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(title, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 12, color: Colors.black87)),
                Text(subtitle, style: TextStyle(color: Colors.grey[600], fontSize: 10)),
              ],
            ),
          ),
          Text(time, style: TextStyle(color: Colors.grey[500], fontSize: 9)),
        ],
      ),
    );
  }
}
