import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    final products = [
      {'name': 'Wireless Headphones', 'description': 'Premium quality sound', 'price': '99.99', 'icon': Icons.headphones, 'gradient': [Colors.purple, Colors.pink]},
      {'name': 'Smartphone', 'description': 'Latest technology device', 'price': '699.99', 'icon': Icons.phone_android, 'gradient': [Colors.blue, Colors.cyan]},
      {'name': 'Laptop Computer', 'description': 'High performance machine', 'price': '1299.99', 'icon': Icons.laptop, 'gradient': [Colors.orange, Colors.red]},
      {'name': 'Smart Watch', 'description': 'Track your fitness goals', 'price': '299.99', 'icon': Icons.watch, 'gradient': [Colors.green, Colors.lightGreen]},
      {'name': 'Gaming Console', 'description': 'Next-gen gaming experience', 'price': '499.99', 'icon': Icons.games, 'gradient': [Colors.indigo, Colors.purple]},
      {'name': 'Bluetooth Speaker', 'description': 'Portable audio solution', 'price': '79.99', 'icon': Icons.speaker, 'gradient': [Colors.teal, Colors.blue]},
    ];

    return Scaffold(
      backgroundColor: Colors.grey[900],
      appBar: AppBar(
        title: Text('Colorful Products', style: TextStyle(color: Colors.white)),
        backgroundColor: Colors.grey[900],
        elevation: 0,
      ),
      body: ListView.builder(
        padding: EdgeInsets.all(16),
        itemCount: products.length,
        itemBuilder: (context, index) {
          final product = products[index];
          final gradientColors = product['gradient'] as List<Color>;
          return Container(
            margin: EdgeInsets.only(bottom: 16),
            decoration: BoxDecoration(
              gradient: LinearGradient(
                colors: gradientColors,
                begin: Alignment.topLeft,
                end: Alignment.bottomRight,
              ),
              borderRadius: BorderRadius.circular(16),
            ),
            child: Padding(
              padding: EdgeInsets.all(24),
              child: Column(
                children: [
                  CircleAvatar(
                    radius: 35,
                    backgroundColor: Colors.white,
                    child: Icon(product['icon'] as IconData, size: 35, color: gradientColors[0]),
                  ),
                  SizedBox(height: 16),
                  Text(
                    product['name'] as String,
                    style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.white),
                    textAlign: TextAlign.center,
                  ),
                  SizedBox(height: 8),
                  Text(
                    product['description'] as String,
                    style: TextStyle(fontSize: 14, color: Colors.white.withOpacity(0.9)),
                    textAlign: TextAlign.center,
                  ),
                  SizedBox(height: 12),
                  Text(
                    product['price'] as String,
                    style: TextStyle(fontSize: 22, fontWeight: FontWeight.bold, color: Colors.white),
                  ),
                  SizedBox(height: 16),
                  ElevatedButton(
                    onPressed: () {},
                    style: ElevatedButton.styleFrom(
                      backgroundColor: Colors.white,
                      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(25)),
                      padding: EdgeInsets.symmetric(horizontal: 32, vertical: 12),
                    ),
                    child: Text('Add to Cart', style: TextStyle(color: gradientColors[0], fontWeight: FontWeight.bold)),
                  ),
                ],
              ),
            ),
          );
        },
      ),
    );
  }
}
