  Widget _buildProgressStep(String label, bool isCompleted, bool isCurrent) {
    return Column(
      children: [
        Container(
          width: 24,
          height: 24,
          decoration: BoxDecoration(
            color: isCompleted ? Colors.green : (isCurrent ? Colors.blue : Colors.grey[300]),
            shape: BoxShape.circle,
          ),
          child: isCompleted
              ? Icon(Icons.check, color: Colors.white, size: 16)
              : (isCurrent ? Icon(Icons.local_shipping, color: Colors.white, size: 12) : null),
        ),
        SizedBox(height: 8),
        Text(label, style: TextStyle(fontSize: 12, color: isCompleted || isCurrent ? Colors.black : Colors.grey[600])),
      ],
    );
  }

  Widget _buildInfoSection(String title, List<String> items) {
    return Container(
      width: double.infinity,
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: Colors.white,
        borderRadius: BorderRadius.circular(12),
        boxShadow: [BoxShadow(color: Colors.grey[200]!, blurRadius: 8, offset: Offset(0, 2))],
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Text(title, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 16)),
          SizedBox(height: 12),
          ...items.map((item) => Padding(
            padding: EdgeInsets.only(bottom: 4),
            child: Text(item, style: TextStyle(color: Colors.grey[700])),
          )),
        ],
      ),
    );
  }
}
