import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  int _selectedIndex = 0;
  List<Map<String, dynamic>> tasks = [
    {'title': 'FINISH REPORT', 'description': 'Complete quarterly report', 'priority': 'High', 'dueDate': '2025-08-10', 'completed': false, 'priorityColor': Colors.red},
    {'title': 'TEAM MEETING', 'description': 'Discuss project milestones', 'priority': 'Medium', 'dueDate': '2025-08-12', 'completed': true, 'priorityColor': Colors.orange},
    {'title': 'UPDATE DOCS', 'description': 'Revise API documentation', 'priority': 'Low', 'dueDate': '2025-08-15', 'completed': false, 'priorityColor': Colors.green}
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black,
      appBar: AppBar(
        title: Text('Bold Tasks', style: TextStyle(fontWeight: FontWeight.bold, fontSize: 24, fontFamily: 'Roboto')),
        backgroundColor: Colors.red,
        foregroundColor: Colors.white,
        elevation: 2,
        actions: [IconButton(icon: Icon(Icons.notifications), onPressed: () {})],
      ),
      body: ListView.builder(
        padding: EdgeInsets.all(16),
        itemCount: tasks.length,
        itemBuilder: (context, index) {
          final task = tasks[index];
          return Card(
            margin: EdgeInsets.only(bottom: 12),
            elevation: 0,
            color: Colors.white,
            shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
            child: Padding(
              padding: EdgeInsets.all(16),
              child: Row(
                children: [
                  Checkbox(
                    value: task['completed'],
                    onChanged: (value) { setState(() { task['completed'] = value!; }); },
                    activeColor: Colors.red,
                    checkColor: Colors.black,
                  ),
                  SizedBox(width: 12),
                  Expanded(
                    child: Column(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      children: [
                        Text(
                          task['title'],
                          style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.black, decoration: task['completed'] ? TextDecoration.lineThrough : null),
                        ),
                        SizedBox(height: 4),
                        Text(
                          task['description'],
                          style: TextStyle(fontSize: 14, color: Colors.grey[800]),
                        ),
                        SizedBox(height: 8),
                        Row(
                          children: [
                            Container(
                              padding: EdgeInsets.symmetric(horizontal: 8, vertical: 4),
                              decoration: BoxDecoration(color: task['priorityColor'], borderRadius: BorderRadius.circular(12)),
                              child: Text(task['priority'], style: TextStyle(color: Colors.white, fontSize: 12)),
                            ),
                            SizedBox(width: 12),
                            Icon(Icons.calendar_today, size: 16, color: Colors.grey[800]),
                            SizedBox(width: 4),
                            Text(task['dueDate'], style: TextStyle(fontSize: 12, color: Colors.grey[800])),
                          ],
                        ),
                      ],
                    ),
                  ),
                  IconButton(icon: Icon(Icons.more_vert, color: Colors.black), onPressed: () {}),
                ],
              ),
            ),
          );
        },
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {},
        backgroundColor: Colors.red,
        child: Icon(Icons.add, color: Colors.white, size: 32),
      ),
      bottomNavigationBar: BottomNavigationBar(
        backgroundColor: Colors.black,
        selectedItemColor: Colors.red,
        unselectedItemColor: Colors.white70,
        currentIndex: _selectedIndex,
        onTap: (index) { setState(() { _selectedIndex = index; }); },
        items: [
          BottomNavigationBarItem(icon: Icon(Icons.task_alt), label: 'Tasks'),
          BottomNavigationBarItem(icon: Icon(Icons.folder_outlined), label: 'Projects'),
          BottomNavigationBarItem(icon: Icon(Icons.person_outline), label: 'Profile'),
        ],
      ),
    );
  }
}
