import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Container(
        decoration: BoxDecoration(
          gradient: LinearGradient(
            begin: Alignment.topCenter,
            end: Alignment.bottomCenter,
            colors: [Color(0xFF1E3C72), Color(0xFF2A5298)],
          ),
        ),
        child: SafeArea(
          child: Column(
            children: [
              Padding(
                padding: EdgeInsets.all(20),
                child: Row(
                  mainAxisAlignment: MainAxisAlignment.center,
                  children: [
                    Icon(Icons.emoji_events, color: Color(0xFFFFD700), size: 32),
                    SizedBox(width: 12),
                    Text(
                      'Leaderboard',
                      style: TextStyle(
                        fontSize: 28,
                        fontWeight: FontWeight.bold,
                        color: Colors.white,
                      ),
                    ),
                  ],
                ),
              ),
              Container(
                height: 200,
                padding: EdgeInsets.symmetric(horizontal: 20),
                child: Row(
                  mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                  crossAxisAlignment: CrossAxisAlignment.end,
                  children: [
                    _buildPodiumPlayer('Alice', '2,450', 2, Color(0xFFC0C0C0)),
                    _buildPodiumPlayer('John', '2,890', 1, Color(0xFFFFD700)),
                    _buildPodiumPlayer('Bob', '2,120', 3, Color(0xFFCD7F32)),
                  ],
                ),
              ),
              SizedBox(height: 20),
              Expanded(
                child: Container(
                  decoration: BoxDecoration(
                    color: Colors.white,
                    borderRadius: BorderRadius.vertical(top: Radius.circular(25)),
                  ),
                  child: ListView.builder(
                    padding: EdgeInsets.all(20),
                    itemCount: 7,
                    itemBuilder: (context, index) {
                      List<Map<String, dynamic>> players = [
                        {'name': 'Emma', 'score': '1,980'},
                        {'name': 'Mike', 'score': '1,850'},
                        {'name': 'Sarah', 'score': '1,720'},
                        {'name': 'David', 'score': '1,650'},
                        {'name': 'Lisa', 'score': '1,580'},
                        {'name': 'Tom', 'score': '1,420'},
                        {'name': 'Anna', 'score': '1,350'},
                      ];
                      return _buildPlayerCard(players[index]['name'], players[index]['score'], index + 4);
                    },
                  ),
                ),
              ),
            ],
          ),
        ),
      ),
    );
  }

  Widget _buildPodiumPlayer(String name, String score, int rank, Color medalColor) {
    double height = rank == 1 ? 120 : (rank == 2 ? 100 : 80);
    return Column(
      mainAxisAlignment: MainAxisAlignment.end,
      children: [
        Stack(
          alignment: Alignment.center,
          children: [
            CircleAvatar(
              radius: 30,
              backgroundColor: Colors.white,
              child: Icon(Icons.person, size: 40, color: Colors.grey[600]),
            ),
            Positioned(
              top: -5,
              right: -5,
              child: Container(
                padding: EdgeInsets.all(4),
                decoration: BoxDecoration(
                  color: medalColor,
                  shape: BoxShape.circle,
                ),
                child: Text(
                  rank.toString(),
                  style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold, fontSize: 12),
                ),
              ),
            ),
          ],
        ),
        SizedBox(height: 8),
        Text(name, style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold)),
        Text(score, style: TextStyle(color: Colors.white70, fontSize: 12)),
        SizedBox(height: 10),
        Container(
          width: 60,
          height: height,
          decoration: BoxDecoration(
            color: medalColor.withOpacity(0.3),
            borderRadius: BorderRadius.vertical(top: Radius.circular(8)),
          ),
        ),
      ],
    );
  }

  Widget _buildPlayerCard(String name, String score, int rank) {
    return Container(
      margin: EdgeInsets.only(bottom: 12),
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: Colors.grey[50],
        borderRadius: BorderRadius.circular(12),
        boxShadow: [
          BoxShadow(
            color: Colors.black.withOpacity(0.05),
            blurRadius: 5,
            offset: Offset(0, 2),
          ),
        ],
      ),
      child: Row(
        children: [
          Container(
            width: 30,
            height: 30,
            decoration: BoxDecoration(
              color: Colors.grey[300],
              shape: BoxShape.circle,
            ),
            child: Center(
              child: Text(
                rank.toString(),
                style: TextStyle(fontWeight: FontWeight.bold, color: Colors.grey[700]),
              ),
            ),
          ),
          SizedBox(width: 16),
          CircleAvatar(
            radius: 20,
            backgroundColor: Colors.blue[100],
            child: Icon(Icons.person, color: Colors.blue[600]),
          ),
          SizedBox(width: 16),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  name,
                  style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16),
                ),
                Text(
                  'Points',
                  style: TextStyle(color: Colors.grey[600], fontSize: 12),
                ),
              ],
            ),
          ),
          Text(
            score,
            style: TextStyle(fontWeight: FontWeight.bold, fontSize: 18, color: Colors.blue[600]),
          ),
        ],
      ),
    );
  }
}
