import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Container(
        decoration: BoxDecoration(
          gradient: LinearGradient(
            begin: Alignment.topCenter,
            end: Alignment.bottomCenter,
            colors: [Color(0xFF0A1C3A), Color(0xFF000000)],
          ),
        ),
        child: SafeArea(
          child: Column(
            children: [
              Padding(
                padding: EdgeInsets.all(16),
                child: Row(
                  mainAxisAlignment: MainAxisAlignment.center,
                  children: [
                    Icon(Icons.emoji_events, color: Color(0xFFFFD700), size: 30),
                    SizedBox(width: 10),
                    Text(
                      'Leaderboard',
                      style: TextStyle(
                        fontFamily: 'Roboto',
                        fontSize: 26,
                        fontWeight: FontWeight.bold,
                        color: Colors.white,
                      ),
                    ),
                  ],
                ),
              ),
              Container(
                height: 180,
                padding: EdgeInsets.symmetric(horizontal: 16),
                child: Row(
                  mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                  children: [
                    _buildPodiumPlayer('Alice', '2,450', 2, Color(0xFFC0C0C0)),
                    _buildPodiumPlayer('John', '2,890', 1, Color(0xFFFFD700)),
                    _buildPodiumPlayer('Bob', '2,120', 3, Color(0xFFCD7F32)),
                  ],
                ),
              ),
              SizedBox(height: 16),
              Expanded(
                child: ListView.builder(
                  padding: EdgeInsets.all(16),
                  itemCount: 7,
                  itemBuilder: (context, index) {
                    List<Map<String, dynamic>> players = [
                      {'name': 'Emma', 'score': '1,980'},
                      {'name': 'Mike', 'score': '1,850'},
                      {'name': 'Sarah', 'score': '1,720'},
                      {'name': 'David', 'score': '1,650'},
                      {'name': 'Lisa', 'score': '1,580'},
                      {'name': 'Tom', 'score': '1,420'},
                      {'name': 'Anna', 'score': '1,350'},
                    ];
                    return _buildPlayerCard(players[index]['name'], players[index]['score'], index + 4);
                  },
                ),
              ),
            ],
          ),
        ),
      ),
    );
  }

  Widget _buildPodiumPlayer(String name, String score, int rank, Color medalColor) {
    return Column(
      mainAxisAlignment: MainAxisAlignment.end,
      children: [
        Stack(
          alignment: Alignment.center,
          children: [
            CircleAvatar(
              radius: 28,
              backgroundColor: Colors.white,
              child: Icon(Icons.person, size: 36, color: Colors.grey[600]),
            ),
            Positioned(
              top: 0,
              right: 0,
              child: Container(
                padding: EdgeInsets.all(4),
                decoration: BoxDecoration(
                  color: medalColor,
                  shape: BoxShape.circle,
                ),
                child: Text(
                  rank.toString(),
                  style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold, fontSize: 12),
                ),
              ),
            ),
          ],
        ),
        SizedBox(height: 8),
        Text(name, style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold, fontFamily: 'Roboto')),
        Text(score, style: TextStyle(color: Colors.white70, fontSize: 12, fontFamily: 'Roboto')),
      ],
    );
  }

  Widget _buildPlayerCard(String name, String score, int rank) {
    return Container(
      margin: EdgeInsets.only(bottom: 12),
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: Colors.grey[800],
        borderRadius: BorderRadius.circular(10),
        boxShadow: [
          BoxShadow(
            color: Colors.black.withOpacity(0.1),
            blurRadius: 4,
            offset: Offset(0, 2),
          ),
        ],
      ),
      child: Row(
        children: [
          Container(
            width: 28,
            height: 28,
            decoration: BoxDecoration(
              color: Colors.grey[600],
              shape: BoxShape.circle,
            ),
            child: Center(
              child: Text(
                rank.toString(),
                style: TextStyle(fontWeight: FontWeight.bold, color: Colors.white, fontFamily: 'Roboto'),
              ),
            ),
          ),
          SizedBox(width: 12),
          CircleAvatar(
            radius: 18,
            backgroundColor: Colors.blue[200],
            child: Icon(Icons.person, color: Colors.blue[800]),
          ),
          SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(
                  name,
                  style: TextStyle(fontWeight: FontWeight.bold, fontSize: 15, color: Colors.white, fontFamily: 'Roboto'),
                ),
                Text(
                  'Points',
                  style: TextStyle(color: Colors.white70, fontSize: 12, fontFamily: 'Roboto'),
                ),
              ],
            ),
          ),
          Text(
            score,
            style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16, color: Colors.blue[400], fontFamily: 'Roboto'),
          ),
        ],
      ),
    );
  }
}
