import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool isFollowersSelected = true;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      appBar: AppBar(
        backgroundColor: Colors.white,
        elevation: 0,
        leading: Icon(Icons.arrow_back, color: Colors.black),
        title: Text('Followers', style: TextStyle(color: Colors.black, fontWeight: FontWeight.bold)),
        actions: [Icon(Icons.search, color: Colors.black), SizedBox(width: 12)],
        centerTitle: true,
      ),
      body: Column(
        children: [
          Container(
            padding: EdgeInsets.all(12),
            child: Row(
              mainAxisAlignment: MainAxisAlignment.center,
              children: [
                GestureDetector(
                  onTap: () => setState(() => isFollowersSelected = false),
                  child: Container(
                    padding: EdgeInsets.symmetric(vertical: 8),
                    decoration: BoxDecoration(
                      border: Border(bottom: BorderSide(color: !isFollowersSelected ? Colors.black : Colors.transparent, width: 2)),
                    ),
                    child: Text(
                      'Following',
                      style: TextStyle(
                        color: !isFollowersSelected ? Colors.black : Colors.grey,
                        fontWeight: FontWeight.w600,
                      ),
                    ),
                  ),
                ),
                SizedBox(width: 16),
                GestureDetector(
                  onTap: () => setState(() => isFollowersSelected = true),
                  child: Container(
                    padding: EdgeInsets.symmetric(vertical: 8),
                    decoration: BoxDecoration(
                      border: Border(bottom: BorderSide(color: isFollowersSelected ? Colors.black : Colors.transparent, width: 2)),
                    ),
                    child: Text(
                      'Followers',
                      style: TextStyle(
                        color: isFollowersSelected ? Colors.black : Colors.grey,
                        fontWeight: FontWeight.w600,
                      ),
                    ),
                  ),
                ),
              ],
            ),
          ),
          Expanded(
            child: ListView.builder(
              padding: EdgeInsets.symmetric(horizontal: 12),
              itemCount: 10,
              itemBuilder: (context, index) {
                return ListTile(
                  contentPadding: EdgeInsets.symmetric(vertical: 8),
                  leading: CircleAvatar(
                    radius: 25,
                    backgroundColor: Colors.grey[300],
                    backgroundImage: NetworkImage('https://picsum.photos/50/50?random=$index'),
                  ),
                  title: Text(
                    'User Name ${index + 1}',
                    style: TextStyle(fontWeight: FontWeight.bold, color: Colors.black),
                  ),
                  subtitle: Text(
                    '@username${index + 1}',
                    style: TextStyle(color: Colors.grey[600]),
                  ),
                  trailing: OutlinedButton(
                    onPressed: () {},
                    style: OutlinedButton.styleFrom(
                      side: BorderSide(color: Colors.black),
                      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(20)),
                      padding: EdgeInsets.symmetric(horizontal: 16, vertical: 8),
                    ),
                    child: Text('Follow', style: TextStyle(color: Colors.black)),
                  ),
                );
              },
            ),
          ),
        ],
      ),
    );
  }
}
