import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool isDarkMode = false;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body: Container(
        decoration: BoxDecoration(
          gradient: LinearGradient(
            colors: [Colors.purple, Colors.blue],
            begin: Alignment.topCenter,
            end: Alignment.bottomCenter,
          ),
        ),
        child: Center(
          child: Container(
            decoration: BoxDecoration(
              color: Colors.grey[200],
              borderRadius: BorderRadius.circular(12),
              boxShadow: [
                BoxShadow(color: Colors.white, offset: Offset(-4, -4), blurRadius: 8),
                BoxShadow(color: Colors.black26, offset: Offset(4, 4), blurRadius: 8),
              ],
            ),
            padding: EdgeInsets.all(24),
            child: Column(
              mainAxisSize: MainAxisSize.min,
              children: [
                AnimatedCrossFade(
                  firstChild: Icon(Icons.wb_sunny, color: Colors.yellow, size: 40),
                  secondChild: Icon(Icons.nightlight_round, color: Colors.red, size: 40),
                  crossFadeState: isDarkMode ? CrossFadeState.showSecond : CrossFadeState.showFirst,
                  duration: Duration(milliseconds: 300),
                ),
                SizedBox(height: 16),
                Row(
                  mainAxisSize: MainAxisSize.min,
                  children: [
                    Text(
                      'Light',
                      style: TextStyle(
                        fontSize: 18,
                        fontFamily: 'Comic Sans MS',
                        color: Colors.yellow,
                      ),
                    ),
                    SizedBox(width: 16),
                    AnimatedContainer(
                      duration: Duration(milliseconds: 300),
                      curve: Curves.bounceOut,
                      width: 80,
                      height: 40,
                      decoration: BoxDecoration(
                        borderRadius: BorderRadius.circular(10),
                        color: isDarkMode ? Colors.red : Colors.yellow,
                      ),
                      child: Stack(
                        children: [
                          AnimatedAlign(
                            duration: Duration(milliseconds: 300),
                            alignment: isDarkMode ? Alignment.centerRight : Alignment.centerLeft,
                            child: Container(
                              width: 36,
                              height: 36,
                              margin: EdgeInsets.all(2),
                              decoration: BoxDecoration(
                                borderRadius: BorderRadius.circular(8),
                                color: Colors.white,
                              ),
                            ),
                          ),
                        ],
                      ),
                    ),
                    SizedBox(width: 16),
                    Text(
                      'Dark',
                      style: TextStyle(
                        fontSize: 18,
                        fontFamily: 'Comic Sans MS',
                        color: Colors.red,
                      ),
                    ),
                  ],
                ),
                SizedBox(height: 16),
                GestureDetector(
                  onTap: () {
                    setState(() {
                      isDarkMode = !isDarkMode;
                    });
                  },
                  child: Container(
                    padding: EdgeInsets.symmetric(horizontal: 20, vertical: 10),
                    decoration: BoxDecoration(
                      color: Colors.blue,
                      borderRadius: BorderRadius.circular(8),
                    ),
                    child: Text(
                      'Toggle',
                      style: TextStyle(color: Colors.white, fontSize: 16),
                    ),
                  ),
                ),
              ],
            ),
          ),
        ),
      ),
    );
  }
}
