class HeartClipper extends CustomClipper<Path> {
  @override
  Path getClip(Size size) {
    final path = Path();
    double w = size.width;
    double h = size.height;
    path.moveTo(0.5 * w, h * 0.35);
    path.cubicTo(0.2 * w, h * 0.1, -0.25 * w, h * 0.6, 0.5 * w, h);
    path.moveTo(0.5 * w, h * 0.35);
    path.cubicTo(0.8 * w, h * 0.1, 1.25 * w, h * 0.6, 0.5 * w, h);
    return path;
  }
  @override
  bool shouldReclip(CustomClipper<Path> oldClipper) => true;
}

class StarClipper extends CustomClipper<Path> {
  @override
  Path getClip(Size size) {
    final path = Path();
    double w = size.width;
    double h = size.height;
    for (int i = 0; i < 5; i++) {
      double angle = (i / 5) * 2 * 3.14159 - 3.14159 / 2;
      double innerAngle = angle + 3.14159 / 5;
      path.lineTo(w / 2 + w / 2 * math.cos(angle), h / 2 + h / 2 * math.sin(angle));
      path.lineTo(w / 2 + w / 4 * math.cos(innerAngle), h / 2 + h / 4 * math.sin(innerAngle));
    }
    path.close();
    return path;
  }
  @override
  bool shouldReclip(CustomClipper<Path> oldClipper) => true;
}
