class LeafClipper extends CustomClipper<Path> {
  @override
  Path getClip(Size size) {
    final path = Path();
    double w = size.width;
    double h = size.height;
    path.moveTo(0.5 * w, 0);
    path.quadraticBezierTo(0, h * 0.4, 0.3 * w, h * 0.7);
    path.quadraticBezierTo(0.5 * w, h, 0.7 * w, h * 0.7);
    path.quadraticBezierTo(w, h * 0.4, 0.5 * w, 0);
    path.close();
    return path;
  }
  @override
  bool shouldReclip(CustomClipper<Path> oldClipper) => true;
}
