import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  final _companyController = TextEditingController();
  final _clientController = TextEditingController();
  final _invoiceController = TextEditingController(text: 'INV-001');
  final _descriptionController = TextEditingController();
  final _itemController = TextEditingController();
  final _quantityController = TextEditingController();
  final _priceController = TextEditingController();
  DateTime _selectedDate = DateTime.now();
  double _total = 0.0;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      appBar: AppBar(
        title: Text('Invoice Generator', style: TextStyle(color: Colors.black87)),
        backgroundColor: Colors.white,
        elevation: 0,
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(20),
        child: Container(
          padding: EdgeInsets.all(20),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.stretch,
            children: [
              Text('Invoice Generator', style: TextStyle(fontSize: 28, fontWeight: FontWeight.bold, color: Color(0xFFF06292))),
              SizedBox(height: 16),
              TextField(
                controller: _companyController,
                decoration: InputDecoration(
                  labelText: 'Company Name',
                  labelStyle: TextStyle(color: Color(0xFFF06292)),
                  enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                  focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                ),
              ),
              SizedBox(height: 16),
              TextField(
                controller: _clientController,
                decoration: InputDecoration(
                  labelText: 'Client Name',
                  labelStyle: TextStyle(color: Color(0xFFF06292)),
                  enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                  focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                ),
              ),
              SizedBox(height: 16),
              TextField(
                controller: _invoiceController,
                decoration: InputDecoration(
                  labelText: 'Invoice Number',
                  labelStyle: TextStyle(color: Color(0xFFF06292)),
                  enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                  focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                ),
              ),
              SizedBox(height: 16),
              Row(
                children: [
                  Expanded(
                    child: Text('Date: ${_selectedDate.day}/${_selectedDate.month}/${_selectedDate.year}', style: TextStyle(fontSize: 16)),
                  ),
                  IconButton(
                    icon: Icon(Icons.calendar_today, color: Color(0xFF64B5F6)),
                    onPressed: () async {
                      final date = await showDatePicker(context: context, initialDate: _selectedDate, firstDate: DateTime(2020), lastDate: DateTime(2030));
                      if (date != null) setState(() => _selectedDate = date);
                    },
                  ),
                ],
              ),
              SizedBox(height: 16),
              TextField(
                controller: _descriptionController,
                maxLines: 3,
                decoration: InputDecoration(
                  labelText: 'Description',
                  labelStyle: TextStyle(color: Colors.grey[600]),
                  border: OutlineInputBorder(borderRadius: BorderRadius.circular(8), borderSide: BorderSide(color: Colors.grey[300]!)),
                  focusedBorder: OutlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                ),
              ),
              SizedBox(height: 16),
              Text('Add Item', style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold, color: Color(0xFFF06292))),
              SizedBox(height: 16),
              TextField(
                controller: _itemController,
                decoration: InputDecoration(
                  labelText: 'Item Name',
                  labelStyle: TextStyle(color: Color(0xFFF06292)),
                  enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                  focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                ),
              ),
              SizedBox(height: 16),
              TextField(
                controller: _quantityController,
                keyboardType: TextInputType.number,
                decoration: InputDecoration(
                  labelText: 'Qty',
                  labelStyle: TextStyle(color: Color(0xFFF06292)),
                  enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                  focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                ),
              ),
              SizedBox(height: 16),
              TextField(
                controller: _priceController,
                keyboardType: TextInputType.numberWithOptions(decimal: true),
                decoration: InputDecoration(
                  labelText: 'Price',
                  labelStyle: TextStyle(color: Color(0xFFF06292)),
                  enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                  focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Color(0xFFF06292))),
                ),
              ),
              SizedBox(height: 16),
              Container(
                padding: EdgeInsets.all(16),
                decoration: BoxDecoration(
                  color: Color(0xFFA5D6A7),
                  borderRadius: BorderRadius.circular(8),
                ),
                child: Text('Total: $${_total.toStringAsFixed(2)}', style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.black87)),
              ),
              SizedBox(height: 16),
              ElevatedButton(
                onPressed: () {},
                style: ElevatedButton.styleFrom(
                  backgroundColor: Color(0xFFF06292),
                  padding: EdgeInsets.symmetric(vertical: 16),
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
                ),
                child: Text('Generate Invoice', style: TextStyle(fontSize: 18, color: Colors.white)),
              ),
            ],
          ),
        ),
      ),
    );
  }
}
