      backgroundColor: Colors.grey[200],
      appBar: AppBar(
        backgroundColor: Colors.grey[200],
        elevation: 0,
        leading: Builder(
          builder: (context) => IconButton(
            icon: Icon(Icons.menu, color: Colors.black54),
            onPressed: () => Scaffold.of(context).openDrawer(),
          ),
        ),
        title: Text('Overview', style: TextStyle(color: Colors.black54, fontWeight: FontWeight.w500)),
        actions: [
          Icon(Icons.notifications_outlined, color: Colors.black54),
          SizedBox(width: 16),
        ],
      ),
      drawer: Drawer(
        child: ListView(
          padding: EdgeInsets.zero,
          children: [
            DrawerHeader(
              decoration: BoxDecoration(color: Colors.grey[300]),
              child: Text('Menu', style: TextStyle(color: Colors.black54, fontSize: 20)),
            ),
            ListTile(title: Text('Home'), leading: Icon(Icons.home)),
            ListTile(title: Text('Settings'), leading: Icon(Icons.settings)),
          ],
        ),
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(12),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text('Hi, Emma!', style: TextStyle(fontSize: 22, fontWeight: FontWeight.w500, color: Colors.black54)),
            SizedBox(height: 12),
            GridView.count(
              crossAxisCount: 2,
              shrinkWrap: true,
              physics: NeverScrollableScrollPhysics(),
              crossAxisSpacing: 12,
              mainAxisSpacing: 12,
              children: [
                _buildMetricCard('4,567', 'Orders', Color(0xFFE6FFFA), Colors.teal[700]!, Icons.trending_up),
                _buildMetricCard('1,890', 'Users', Color(0xFFFFF3E0), Colors.orange[700]!, Icons.person_add),
                _buildMetricCard('88%', 'Feedback', Color(0xFFF3E8FF), Colors.purple[700]!, Icons.star),
                _buildMetricCard('\$10K', 'Revenue', Color(0xFFE0F7FA), Colors.blue[700]!, Icons.monetization_on),
              ],
            ),
            SizedBox(height: 16),
            Text('Recent Updates', style: TextStyle(fontSize: 16, fontWeight: FontWeight.w500, color: Colors.black54)),
            SizedBox(height: 8),
            _buildActivityItem(Icons.shopping_cart, 'Order Received', 'Order #3456', '2 min ago'),
            _buildActivityItem(Icons.person, 'New User', 'Emma Brown joined', '5 min ago'),
            _buildActivityItem(Icons.payment, 'Payment', '\$399.99', '10 min ago'),
          ],
        ),
      ),
    );
  }

  Widget _buildMetricCard(String value, String label, Color bgColor, Color iconColor, IconData icon) {
    return Container(
      padding: EdgeInsets.all(12),
      decoration: BoxDecoration(
        color: bgColor,
        borderRadius: BorderRadius.circular(8),
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Row(
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: [
              Icon(icon, color: iconColor, size: 20),
              Icon(Icons.more_vert, color: Colors.grey[600], size: 14),
            ],
          ),
          SizedBox(height: 8),
          Text(value, style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600, color: Colors.black54)),
          Text(label, style: TextStyle(fontSize: 12, color: Colors.grey[600])),
        ],
      ),
    );
  }

  Widget _buildActivityItem(IconData icon, String title, String subtitle, String time) {
    return Container(
      margin: EdgeInsets.only(bottom: 8),
      padding: EdgeInsets.all(8),
      decoration: BoxDecoration(
        color: Colors.white,
        borderRadius: BorderRadius.circular(4),
      ),
      child: Row(
        children: [
          CircleAvatar(
            radius: 16,
            backgroundColor: Colors.grey[300],
            child: Icon(icon, color: Colors.black54, size: 16),
          ),
          SizedBox(width: 8),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(title, style: TextStyle(fontWeight: FontWeight.w500, fontSize: 13, color: Colors.black54)),
                Text(subtitle, style: TextStyle(color: Colors.grey[600], fontSize: 11)),
              ],
            ),
          ),
          Text(time, style: TextStyle(color: Colors.grey[500], fontSize: 10)),
        ],
      ),
    );
  }
}
