  Widget _buildProgressStep(String label, bool isCompleted, bool isCurrent) {
    return Column(
      children: [
        Container(
          width: 24,
          height: 24,
          decoration: BoxDecoration(
            color: isCompleted ? Colors.green : (isCurrent ? Colors.yellow[700] : Colors.grey[700]),
            shape: BoxShape.circle,
          ),
          child: isCompleted
              ? Icon(Icons.check, color: Colors.white, size: 16)
              : (isCurrent ? Icon(Icons.local_shipping, color: Colors.black, size: 12) : null),
        ),
        SizedBox(height: 8),
        Text(label, style: TextStyle(fontSize: 12, color: isCompleted || isCurrent ? Colors.white : Colors.grey[500])),
      ],
    );
  }

  Widget _buildInfoSection(String title, List<String> items, Color textColor) {
