        children: [
          Text(title, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 16, color: textColor)),
          SizedBox(height: 12),
          ...items.map((item) => Padding(
            padding: EdgeInsets.only(bottom: 4),
            child: Text(item, style: TextStyle(color: textColor.withOpacity(0.8))),
