  Widget _buildTimelineStep(String label, bool isCompleted, {bool isCurrent = false}) {
    return Row(
      children: [
        Column(
          children: [
            Container(
              width: 24,
              height: 24,
              decoration: BoxDecoration(
                color: isCompleted ? Colors.green : (isCurrent ? Colors.purple : Colors.grey[300]),
                shape: BoxShape.circle,
              ),
              child: isCompleted
                  ? Icon(Icons.check, color: Colors.white, size: 16)
                  : (isCurrent ? Icon(Icons.local_shipping, color: Colors.white, size: 12) : null),
            ),
            if (!isCurrent) Container(width: 2, height: 40, color: isCompleted ? Colors.green : Colors.grey[300]),
          ],
        ),
        SizedBox(width: 16),
        Text(label, style: TextStyle(fontSize: 14, fontWeight: isCurrent ? FontWeight.bold : FontWeight.normal)),
      ],
    );
  }

