import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  List<Map<String, dynamic>> wishlistItems = [
    {'name': 'Wireless Headphones', 'price': '99.99', 'icon': Icons.headphones},
    {'name': 'Smartphone', 'price': '699.99', 'icon': Icons.phone_android},
    {'name': 'Laptop', 'price': '1299.99', 'icon': Icons.laptop},
    {'name': 'Watch', 'price': '299.99', 'icon': Icons.watch},
  ];

  void removeItem(int index) {
    setState(() {
      wishlistItems.removeAt(index);
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFF0A1F44),
      appBar: AppBar(
        backgroundColor: Color(0xFF0A1F44),
        elevation: 0,
        title: Row(
          children: [
            Icon(Icons.favorite, color: Colors.cyanAccent, size: 24),
            SizedBox(width: 8),
            Text('Wishlist', style: TextStyle(color: Colors.greenAccent, fontSize: 20, fontFamily: 'Orbitron')),
          ],
        ),
      ),
      body: wishlistItems.isEmpty
          ? Center(
              child: Column(
                mainAxisAlignment: MainAxisAlignment.center,
                children: [
                  Icon(Icons.favorite_border, size: 80, color: Colors.cyanAccent.withOpacity(0.5)),
                  SizedBox(height: 16),
                  Text('Your wishlist is empty', style: TextStyle(fontSize: 18, color: Colors.cyanAccent, fontFamily: 'Orbitron')),
                ],
              ),
            )
          : GridView.builder(
              padding: EdgeInsets.all(16),
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 2,
                crossAxisSpacing: 12,
                mainAxisSpacing: 12,
                childAspectRatio: 0.8,
              ),
              itemCount: wishlistItems.length,
              itemBuilder: (context, index) {
                final item = wishlistItems[index];
                return Container(
                  decoration: BoxDecoration(
                    color: Colors.white.withOpacity(0.1),
                    borderRadius: BorderRadius.circular(12),
                    border: Border.all(color: Colors.cyanAccent.withOpacity(0.3)),
                  ),
                  child: ClipRRect(
                    borderRadius: BorderRadius.circular(12),
                    child: BackdropFilter(
                      filter: ImageFilter.blur(sigmaX: 5, sigmaY: 5),
                      child: Column(
                        mainAxisAlignment: MainAxisAlignment.center,
                        children: [
                          Align(
                            alignment: Alignment.topRight,
                            child: IconButton(
                              icon: Icon(Icons.favorite, color: Colors.cyanAccent, size: 20),
                              onPressed: () => removeItem(index),
                            ),
                          ),
                          Icon(item['icon'], size: 40, color: Colors.greenAccent),
                          SizedBox(height: 8),
                          Text(
                            item['name'],
                            style: TextStyle(fontSize: 14, fontWeight: FontWeight.w500, color: Colors.white, fontFamily: 'Orbitron'),
                            textAlign: TextAlign.center,
                          ),
                          SizedBox(height: 4),
                          Text(
                            item['price'],
                            style: TextStyle(fontSize: 12, color: Colors.greenAccent, fontWeight: FontWeight.w600),
                          ),
                        ],
                      ),
                    ),
                  ),
                );
              },
            ),
      floatingActionButton: FloatingActionButton(
        backgroundColor: Colors.pinkAccent,
        child: Icon(Icons.add, color: Colors.black),
        onPressed: () {},
      ),
    );
  }
}
