  Widget _buildProgressStep(String label, bool isCompleted, {bool isCurrent = false}) {
    return Column(
      children: [
        Container(
          width: 24,
          height: 24,
          decoration: BoxDecoration(
            color: isCompleted ? Colors.cyan : (isCurrent ? Colors.cyan : Colors.grey[700]),
            shape: BoxShape.circle,
            border: isCurrent ? Border.all(color: Colors.cyanAccent, width: 2) : null,
            boxShadow: isCurrent ? [BoxShadow(color: Colors.cyan.withOpacity(0.5), blurRadius: 8)] : [],
          ),
          child: isCompleted
              ? Icon(Icons.check, color: Colors.black, size: 16)
              : (isCurrent ? Icon(Icons.local_shipping, color: Colors.black, size: 12) : null),
        ),
        SizedBox(height: 8),
        Text(label, style: TextStyle(fontSize: 12, color: isCompleted || isCurrent ? Colors.cyan : Colors.grey[500])),
      ],
    );
  }

  Widget _buildInfoSection(String title, List<String> items, Color textColor) {
    return Container(
      width: double.infinity,
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        border: Border.all(color: Colors.cyan, width: 2),
        borderRadius: BorderRadius.circular(12),
        boxShadow: [BoxShadow(color: Colors.cyan.withOpacity(0.3), blurRadius: 12)],
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Text(title, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 16, color: textColor)),
          SizedBox(height: 12),
          ...items.map((item) => Padding(
            padding: EdgeInsets.only(bottom: 4),
            child: Text(item, style: TextStyle(color: textColor.withOpacity(0.8))),
          )),
        ],
      ),
    );
  }
