import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  int _selectedIndex = 0;
  List<Map<String, dynamic>> tasks = [
    {
      'title': 'Complete project proposal',
      'description': 'Finalize the quarterly project proposal document',
      'priority': 'H',
      'dueDate': 'Mar 15',
      'completed': false,
    },
    {
      'title': 'Review team feedback',
      'description': 'Go through feedback from last sprint review',
      'priority': 'M',
      'dueDate': 'Mar 12',
      'completed': true,
    },
    {
      'title': 'Update documentation',
      'description': 'Update API documentation with recent changes',
      'priority': 'L',
      'dueDate': 'Mar 18',
      'completed': false,
    }
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      appBar: AppBar(
        title: Text(
          'Tasks',
          style: TextStyle(
            fontSize: 24,
            fontWeight: FontWeight.w300,
            color: Colors.black87,
            letterSpacing: 0.5,
          ),
        ),
        backgroundColor: Colors.white,
        elevation: 0,
        centerTitle: false,
      ),
      body: Column(
        children: [
          Expanded(
            child: ListView.separated(
              padding: EdgeInsets.symmetric(horizontal: 24, vertical: 16),
              itemCount: tasks.length,
              separatorBuilder: (context, index) => Divider(
                color: Colors.grey[200],
                height: 32,
              ),
              itemBuilder: (context, index) {
                final task = tasks[index];
                return Padding(
                  padding: EdgeInsets.symmetric(vertical: 8),
                  child: Row(
                    children: [
                      Container(
                        width: 20,
                        height: 20,
                        decoration: BoxDecoration(
                          shape: BoxShape.circle,
                          border: Border.all(
                            color: task['completed'] ? Color(0xFF4CAF50) : Colors.grey[300]!,
                            width: 2,
                          ),
                          color: task['completed'] ? Color(0xFF4CAF50) : Colors.transparent,
                        ),
                        child: task['completed']
                            ? Icon(Icons.check, size: 14, color: Colors.white)
                            : null,
                      ),
                      SizedBox(width: 16),
                      Expanded(
                        child: Column(
                          crossAxisAlignment: CrossAxisAlignment.start,
                          children: [
                            Text(
                              task['title'],
                              style: TextStyle(
                                fontSize: 16,
                                fontWeight: FontWeight.w400,
                                color: Colors.black87,
                                decoration: task['completed'] ? TextDecoration.lineThrough : null,
                                decorationColor: Colors.grey[400],
                              ),
                            ),
                            SizedBox(height: 4),
                            Text(
                              task['description'],
                              style: TextStyle(
                                fontSize: 14,
                                color: Colors.grey[600],
                                height: 1.3,
                              ),
                            ),
                            SizedBox(height: 8),
                            Row(
                              children: [
                                Container(
                                  width: 24,
                                  height: 24,
                                  decoration: BoxDecoration(
                                    shape: BoxShape.circle,
                                    color: Color(0xFF4CAF50).withOpacity(0.1),
                                  ),
                                  child: Center(
                                    child: Text(
                                      task['priority'],
                                      style: TextStyle(
                                        fontSize: 12,
                                        fontWeight: FontWeight.w500,
                                        color: Color(0xFF4CAF50),
                                      ),
                                    ),
                                  ),
                                ),
                                SizedBox(width: 16),
                                Text(
                                  task['dueDate'],
                                  style: TextStyle(
                                    fontSize: 13,
                                    color: Colors.grey[500],
                                  ),
                                ),
                              ],
                            ),
                          ],
                        ),
                      ),
                    ],
                  ),
                );
              },
            ),
          ),
          Container(
            width: double.infinity,
            padding: EdgeInsets.all(24),
            child: TextButton(
              onPressed: () {},
              style: TextButton.styleFrom(
                padding: EdgeInsets.symmetric(vertical: 16),
                backgroundColor: Color(0xFF4CAF50).withOpacity(0.05),
              ),
              child: Text(
                'Add Task',
                style: TextStyle(
                  fontSize: 16,
                  color: Color(0xFF4CAF50),
                  fontWeight: FontWeight.w500,
                ),
              ),
            ),
          ),
        ],
      ),
      bottomNavigationBar: Container(
        decoration: BoxDecoration(
          border: Border(top: BorderSide(color: Colors.grey[200]!, width: 1)),
        ),
        child: BottomNavigationBar(
          currentIndex: _selectedIndex,
          backgroundColor: Colors.white,
          selectedItemColor: Color(0xFF4CAF50),
          unselectedItemColor: Colors.grey[400],
          elevation: 0,
          type: BottomNavigationBarType.fixed,
          selectedLabelStyle: TextStyle(fontSize: 12, fontWeight: FontWeight.w500),
          unselectedLabelStyle: TextStyle(fontSize: 12),
          onTap: (index) {
            setState(() {
              _selectedIndex = index;
            });
          },
          items: [
            BottomNavigationBarItem(
              icon: SizedBox(),
              label: 'Tasks',
            ),
            BottomNavigationBarItem(
              icon: SizedBox(),
              label: 'Projects',
            ),
            BottomNavigationBarItem(
              icon: SizedBox(),
              label: 'Profile',
            ),
          ],
        ),
      ),
    );
  }
}
