import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  int _selectedIndex = 0;
  List<Map<String, dynamic>> tasks = [
    {'title': 'Finish report', 'description': 'Complete quarterly report', 'priority': 'High', 'dueDate': '2025-08-10', 'completed': false, 'priorityColor': Colors.red},
    {'title': 'Team meeting', 'description': 'Discuss project milestones', 'priority': 'Medium', 'dueDate': '2025-08-12', 'completed': true, 'priorityColor': Colors.yellow},
    {'title': 'Update docs', 'description': 'Revise API documentation', 'priority': 'Low', 'dueDate': '2025-08-15', 'completed': false, 'priorityColor': Colors.green}
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.blue[900],
      appBar: AppBar(
        title: Text('Future Tasks', style: TextStyle(fontWeight: FontWeight.bold, fontFamily: 'Orbitron')),
        backgroundColor: Colors.blue[800],
        foregroundColor: Colors.cyanAccent,
        elevation: 2,
        actions: [IconButton(icon: Icon(Icons.notifications, color: Colors.cyanAccent), onPressed: () {})],
      ),
      body: ListView.builder(
        padding: EdgeInsets.all(16),
        itemCount: tasks.length,
        itemBuilder: (context, index) {
          final task = tasks[index];
          return Card(
            margin: EdgeInsets.only(bottom: 12),
            elevation: 3,
            shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8), side: BorderSide(color: Colors.cyanAccent)),
            color: Colors.blue[850],
            child: Padding(
              padding: EdgeInsets.all(16),
              child: Row(
                children: [
                  Checkbox(
                    value: task['completed'],
                    onChanged: (value) { setState(() { task['completed'] = value!; }); },
                    activeColor: Colors.cyanAccent,
                  ),
                  SizedBox(width: 12),
                  Expanded(
                    child: Column(
                      crossAxisAlignment: CrossAxisAlignment.start,
                      children: [
                        Text(
                          task['title'],
                          style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold, color: Colors.white, decoration: task['completed'] ? TextDecoration.lineThrough : null),
                        ),
                        SizedBox(height: 4),
                        Text(
                          task['description'],
                          style: TextStyle(fontSize: 14, color: Colors.cyanAccent),
                        ),
                        SizedBox(height: 8),
                        Row(
                          children: [
                            Container(
                              padding: EdgeInsets.symmetric(horizontal: 8, vertical: 4),
                              decoration: BoxDecoration(color: task['priorityColor'], borderRadius: BorderRadius.circular(12)),
                              child: Text(task['priority'], style: TextStyle(color: Colors.white, fontSize: 12)),
                            ),
                            SizedBox(width: 12),
                            Icon(Icons.access_time, size: 16, color: Colors.cyanAccent),
                            SizedBox(width: 4),
                            Text(task['dueDate'], style: TextStyle(fontSize: 12, color: Colors.cyanAccent)),
                          ],
                        ),
                      ],
                    ),
                  ),
                  IconButton(icon: Icon(Icons.more_vert, color: Colors.cyanAccent), onPressed: () {}),
                ],
              ),
            ),
          );
        },
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {},
        backgroundColor: Colors.cyanAccent,
        child: Icon(Icons.add, color: Colors.black),
      ),
      bottomNavigationBar: BottomNavigationBar(
        backgroundColor: Colors.blue[850],
        selectedItemColor: Colors.cyanAccent,
        unselectedItemColor: Colors.blue[200],
        currentIndex: _selectedIndex,
        onTap: (index) { setState(() { _selectedIndex = index; }); },
        items: [
          BottomNavigationBarItem(icon: Icon(Icons.task_alt), label: 'Tasks'),
          BottomNavigationBarItem(icon: Icon(Icons.folder_outlined), label: 'Projects'),
          BottomNavigationBarItem(icon: Icon(Icons.person_outline), label: 'Profile'),
        ],
      ),
    );
  }
}
