import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool showAnswer = false;
  int currentCard = 1;
  int totalCards = 10;
  bool isFavorite = false;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      body: Container(
        decoration: BoxDecoration(
          gradient: LinearGradient(colors: [Colors.orange[300]!, Colors.pink[300]!], begin: Alignment.topLeft, end: Alignment.bottomRight),
        ),
        child: Column(
          children: [
            AppBar(
              backgroundColor: Colors.transparent,
              elevation: 0,
              title: Text('History Events', style: TextStyle(fontFamily: 'Pacifico', color: Colors.black87, fontSize: 24)),
              actions: [
                IconButton(icon: Icon(Icons.settings, color: Colors.pink[700]), onPressed: () {}),
                IconButton(icon: Icon(Icons.menu, color: Colors.pink[700]), onPressed: () {}),
              ],
            ),
            Padding(
              padding: EdgeInsets.all(16),
              child: Column(
                children: [
                  Text('$currentCard of $totalCards', style: TextStyle(fontSize: 16, color: Colors.orange[700])),
                  SizedBox(height: 8),
                  LinearProgressIndicator(
                    value: currentCard / totalCards,
                    backgroundColor: Colors.grey[300],
                    valueColor: AlwaysStoppedAnimation<Color>(Colors.orange[700]!),
                  ),
                ],
              ),
            ),
            Expanded(
              child: Center(
                child: Stack(
                  alignment: Alignment.center,
                  children: [
                    Container(
                      width: 280,
                      height: 200,
                      decoration: BoxDecoration(
                        color: Colors.white,
                        shape: BoxShape.rectangle,
                        borderRadius: BorderRadius.all(Radius.circular(20)),
                        boxShadow: [BoxShadow(color: Colors.black12, blurRadius: 8, offset: Offset(0, 4))],
                      ),
                      child: Center(
                        child: Padding(
                          padding: EdgeInsets.all(20),
                          child: Text(
                            showAnswer ? '1776' : 'When was the Declaration of Independence signed?',
                            style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.black87),
                            textAlign: TextAlign.center,
                          ),
                        ),
                      ),
                    ),
                    Positioned(
                      left: 0,
                      child: IconButton(
                        icon: Icon(Icons.chevron_left, size: 40, color: Colors.pink[700]),
                        onPressed: () {},
                      ),
                    ),
                    Positioned(
                      right: 0,
                      child: IconButton(
                        icon: Icon(Icons.chevron_right, size: 40, color: Colors.pink[700]),
                        onPressed: () {},
                      ),
                    ),
                  ],
                ),
              ),
            ),
            Padding(
              padding: EdgeInsets.symmetric(vertical: 20),
              child: ElevatedButton(
                onPressed: () {
                  setState(() {
                    showAnswer = !showAnswer;
                  });
                },
                style: ElevatedButton.styleFrom(
                  backgroundColor: Colors.orange[700],
                  padding: EdgeInsets.symmetric(horizontal: 60, vertical: 12),
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(30)),
                ),
                child: Text('Flip Card', style: TextStyle(color: Colors.white, fontSize: 16)),
              ),
            ),
            Container(
              padding: EdgeInsets.all(16),
              child: Row(
                mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                children: [
                  FloatingActionButton(
                    mini: true,
                    backgroundColor: Colors.orange[700],
                    child: Icon(Icons.shuffle, color: Colors.white),
                    onPressed: () {},
                  ),
                  FloatingActionButton(
                    mini: true,
                    backgroundColor: Colors.orange[700],
                    child: Icon(Icons.refresh, color: Colors.white),
                    onPressed: () {},
                  ),
                  FloatingActionButton(
                    mini: true,
                    backgroundColor: Colors.orange[700],
                    child: Icon(isFavorite ? Icons.favorite : Icons.favorite_border, color: Colors.white),
                    onPressed: () {
                      setState(() {
                        isFavorite = !isFavorite;
                      });
                    },
                  ),
                ],
              ),
            ),
          ],
        ),
      ),
    );
  }
}
