import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  String? selectedLanguage;

  final List<Map<String, String>> languages = [
    {'code': 'en', 'native': 'English', 'english': 'English', 'flag': '🇺🇸'},
    {'code': 'es', 'native': 'Español', 'english': 'Spanish', 'flag': '🇪🇸'},
    {'code': 'fr', 'native': 'Français', 'english': 'French', 'flag': '🇫🇷'},
    {'code': 'de', 'native': 'Deutsch', 'english': 'German', 'flag': '🇩🇪'},
    {'code': 'zh', 'native': '中文', 'english': 'Chinese', 'flag': '🇨🇳'},
    {'code': 'ja', 'native': '日本語', 'english': 'Japanese', 'flag': '🇯🇵'},
    {'code': 'ar', 'native': 'العربية', 'english': 'Arabic', 'flag': '🇸🇦'},
    {'code': 'ru', 'native': 'Русский', 'english': 'Russian', 'flag': '🇷🇺'},
    {'code': 'pt', 'native': 'Português', 'english': 'Portuguese', 'flag': '🇵🇹'},
    {'code': 'it', 'native': 'Italiano', 'english': 'Italian', 'flag': '🇮🇹'},
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      body: SafeArea(
        child: Padding(
          padding: EdgeInsets.all(16),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              Align(
                alignment: Alignment.topRight,
                child: Container(
                  width: 60,
                  height: 60,
                  decoration: BoxDecoration(
                    color: Colors.purple.shade600,
                    shape: BoxShape.circle,
                    boxShadow: [BoxShadow(color: Colors.black12, blurRadius: 8)],
                  ),
                  child: Icon(Icons.language, color: Colors.white, size: 32),
                ),
              ),
              SizedBox(height: 24),
              Text(
                'Language Preference',
                style: TextStyle(
                  fontSize: 26,
                  fontWeight: FontWeight.w600,
                  color: Colors.black,
                ),
              ),
              SizedBox(height: 32),
              SizedBox(
                height: 120,
                child: SingleChildScrollView(
                  scrollDirection: Axis.horizontal,
                  child: Row(
                    children: languages.map((lang) {
                      final isSelected = selectedLanguage == lang['code'];
                      return GestureDetector(
                        onTap: () => setState(() => selectedLanguage = lang['code']),
                        child: Container(
                          width: 100,
                          margin: EdgeInsets.only(right: 12),
                          padding: EdgeInsets.all(10),
                          decoration: BoxDecoration(
                            color: Colors.white,
                            borderRadius: BorderRadius.circular(12),
                            border: Border.all(
                              color: isSelected ? Colors.purple.shade600 : Colors.grey.shade300,
                              width: isSelected ? 2 : 1,
                            ),
                            boxShadow: [BoxShadow(color: Colors.black12, blurRadius: 4)],
                          ),
                          child: Column(
                            mainAxisAlignment: MainAxisAlignment.center,
                            children: [
                              Text(
                                lang['flag']!,
                                style: TextStyle(fontSize: 24),
                              ),
                              SizedBox(height: 8),
                              Text(
                                lang['native']!,
                                style: TextStyle(
                                  fontSize: 14,
                                  fontWeight: FontWeight.w600,
                                  color: Colors.black87,
                                ),
                                textAlign: TextAlign.center,
                              ),
                              Text(
                                lang['english']!,
                                style: TextStyle(
                                  fontSize: 10,
                                  color: Colors.grey.shade600,
                                ),
                                textAlign: TextAlign.center,
                              ),
                            ],
                          ),
                        ),
                      );
                    }).toList(),
                  ),
                ),
              ),
              Spacer(),
              Align(
                alignment: Alignment.bottomLeft,
                child: SizedBox(
                  width: 120,
                  height: 48,
                  child: ElevatedButton(
                    onPressed: selectedLanguage != null ? () {} : null,
                    style: ElevatedButton.styleFrom(
                      backgroundColor: Colors.purple.shade600,
                      foregroundColor: Colors.white,
                      shape: RoundedRectangleBorder(
                        borderRadius: BorderRadius.circular(8),
                      ),
                    ),
                    child: Text(
                      'Continue',
                      style: TextStyle(
                        fontSize: 16,
                        fontWeight: FontWeight.w600,
                      ),
                    ),
                  ),
                ),
              ),
            ],
          ),
        ),
      ),
    );
  }
}
