import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Messages', style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold)),
        backgroundColor: Colors.black,
        elevation: 0,
        actions: [
          IconButton(
            icon: Icon(Icons.search, color: Colors.white),
            onPressed: () {},
          ),
          IconButton(
            icon: Icon(Icons.more_vert, color: Colors.white),
            onPressed: () {},
          ),
        ],
      ),
      backgroundColor: Color(0xFF212121),
      body: ListView.separated(
        itemCount: 15,
        separatorBuilder: (context, index) => Divider(height: 1, color: Colors.grey[800]),
        itemBuilder: (context, index) {
          return ListTile(
            contentPadding: EdgeInsets.symmetric(horizontal: 12, vertical: 6),
            leading: CircleAvatar(
              radius: 24,
              backgroundColor: Colors.teal[300],
              child: Text('U${index + 1}', style: TextStyle(color: Colors.white)),
            ),
            title: Text(
              'User ${index + 1}',
              style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16, color: Colors.white),
            ),
            subtitle: Text(
              'Hey there! How are you doing today?',
              style: TextStyle(color: Colors.grey[400], fontSize: 14),
              maxLines: 1,
              overflow: TextOverflow.ellipsis,
            ),
            trailing: Text(
              '12:${(index % 60).toString().padLeft(2, '0')}',
              style: TextStyle(color: Colors.grey[400], fontSize: 12),
            ),
            onTap: () {},
          );
        },
      ),
    );
  }
}
