import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Conversations', style: TextStyle(color: Colors.white, fontWeight: FontWeight.bold)),
        backgroundColor: Colors.lightBlue,
        elevation: 1,
        actions: [
          IconButton(
            icon: Icon(Icons.search, color: Colors.white),
            onPressed: () {},
          ),
        ],
      ),
      body: Container(
        decoration: BoxDecoration(
          gradient: LinearGradient(
            colors: [Colors.lightBlue[100]!, Colors.white],
            begin: Alignment.topCenter,
            end: Alignment.bottomCenter,
          ),
        ),
        child: ListView.separated(
          itemCount: 15,
          separatorBuilder: (context, index) => Divider(height: 1, color: Colors.grey[400]),
          itemBuilder: (context, index) {
            return ListTile(
              contentPadding: EdgeInsets.symmetric(horizontal: 14, vertical: 7),
              leading: Container(
                width: 52,
                height: 52,
                decoration: BoxDecoration(
                  shape: BoxShape.rectangle,
                  borderRadius: BorderRadius.circular(8),
                  border: Border.all(color: Colors.grey[300]!),
                  color: Colors.blue[100],
                ),
                child: Center(child: Text('U${index + 1}', style: TextStyle(color: Colors.blue[800]))),
              ),
              title: Text(
                'User ${index + 1}',
                style: TextStyle(fontWeight: FontWeight.bold, fontSize: 16, color: Colors.blue[900]),
              ),
              subtitle: Text(
                'Hey there! How are you doing today?',
                style: TextStyle(color: Colors.grey[700], fontSize: 14),
                maxLines: 1,
                overflow: TextOverflow.ellipsis,
              ),
              trailing: Text(
                '12:${(index % 60).toString().padLeft(2, '0')}',
                style: TextStyle(color: Colors.grey[500], fontSize: 12),
              ),
              onTap: () {},
            );
          },
        ),
      ),
    );
  }
}
