import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  bool isRunning = false;
  int seconds = 0;
  String currentTask = 'Luxury Task';
  TextEditingController taskController = TextEditingController();

  String formatTime(int totalSeconds) {
    int hours = totalSeconds ~/ 3600;
    int minutes = (totalSeconds % 3600) ~/ 60;
    int secs = totalSeconds % 60;
    return '${hours.toString().padLeft(2, '0')}:${minutes.toString().padLeft(2, '0')}:${secs.toString().padLeft(2, '0')}';
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black,
      appBar: AppBar(
        title: Text('Time Tracker', style: TextStyle(fontWeight: FontWeight.w600, fontFamily: 'PlayfairDisplay')),
        backgroundColor: Colors.black,
        foregroundColor: Colors.amber[600],
        elevation: 0,
      ),
      body: Padding(
        padding: EdgeInsets.all(16),
        child: Column(
          children: [
            Container(
              padding: EdgeInsets.all(20),
              decoration: BoxDecoration(
                color: Colors.black,
                border: Border.all(color: Colors.amber[600]!, width: 2),
                borderRadius: BorderRadius.circular(12),
              ),
              child: Text(
                formatTime(seconds),
                style: TextStyle(
                  fontSize: 48,
                  fontWeight: FontWeight.w500,
                  fontFamily: 'PlayfairDisplay',
                  color: Colors.amber[600],
                ),
              ),
            ),
            SizedBox(height: 20),
            ElevatedButton(
              onPressed: () => setState(() => isRunning = !isRunning),
              style: ElevatedButton.styleFrom(
                backgroundColor: isRunning ? Colors.red[900] : Colors.green[900],
                padding: EdgeInsets.symmetric(horizontal: 40, vertical: 12),
                shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(12)),
                side: BorderSide(color: Colors.amber[600]!),
              ),
              child: Text(
                isRunning ? 'PAUSE' : 'START',
                style: TextStyle(fontSize: 18, color: Colors.amber[600], fontFamily: 'PlayfairDisplay'),
              ),
            ),
            SizedBox(height: 20),
            TextField(
              controller: taskController,
              decoration: InputDecoration(
                hintText: 'Enter your task',
                filled: true,
                fillColor: Colors.black,
                hintStyle: TextStyle(color: Colors.amber[600], fontFamily: 'PlayfairDisplay'),
                border: OutlineInputBorder(
                  borderRadius: BorderRadius.circular(12),
                  borderSide: BorderSide(color: Colors.amber[600]!, width: 2),
                ),
              ),
              style: TextStyle(color: Colors.amber[600], fontFamily: 'PlayfairDisplay'),
            ),
            SizedBox(height: 16),
            if (isRunning)
              Card(
                color: Colors.red[900],
                shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(12)),
                child: Padding(
                  padding: EdgeInsets.all(12),
                  child: Row(
                    children: [
                      Expanded(
                        child: Column(
                          crossAxisAlignment: CrossAxisAlignment.start,
                          children: [
                            Text(currentTask, style: TextStyle(fontSize: 16, color: Colors.amber[600], fontFamily: 'PlayfairDisplay')),
                            Text('${formatTime(seconds)} elapsed', style: TextStyle(color: Colors.amber[600], fontFamily: 'PlayfairDisplay')),
                          ],
                        ),
                      ),
                      IconButton(
                        onPressed: () => setState(() => isRunning = false),
                        icon: Icon(Icons.stop, color: Colors.amber[600]),
                      ),
                    ],
                  ),
                ),
              ),
            SizedBox(height: 16),
            Expanded(
              child: ListView.separated(
                itemCount: 3,
                separatorBuilder: (context, index) => SizedBox(height: 8),
                itemBuilder: (context, index) => ListTile(
                  title: Text('Task ${index + 1}', style: TextStyle(color: Colors.amber[600], fontFamily: 'PlayfairDisplay')),
                  subtitle: Text('1h ${index * 15}m â€¢ Today', style: TextStyle(color: Colors.amber[600], fontFamily: 'PlayfairDisplay')),
                  tileColor: Colors.black,
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8), side: BorderSide(color: Colors.amber[600]!)),
                ),
              ),
            ),
          ],
        ),
      ),
    );
  }
}
