import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  final _companyController = TextEditingController();
  final _clientController = TextEditingController();
  final _invoiceController = TextEditingController(text: 'INV-001');
  final _descriptionController = TextEditingController();
  final _itemController = TextEditingController();
  final _quantityController = TextEditingController();
  final _priceController = TextEditingController();
  DateTime _selectedDate = DateTime.now();
  double _total = 0.0;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      body: SingleChildScrollView(
        padding: EdgeInsets.symmetric(horizontal: 24, vertical: 40),
        child: Center(
          child: ConstrainedBox(
            constraints: BoxConstraints(maxWidth: 500),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.stretch,
              children: [
                Text(
                  'Invoice Generator',
                  textAlign: TextAlign.center,
                  style: TextStyle(
                    fontSize: 32,
                    fontWeight: FontWeight.w500,
                    color: Colors.orange[600],
                    letterSpacing: -0.5,
                  ),
                ),
                SizedBox(height: 48),
                TextField(
                  controller: _companyController,
                  decoration: InputDecoration(
                    labelText: 'Company Name',
                    labelStyle: TextStyle(color: Colors.grey[600]),
                    border: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                    enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                    focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.orange[600]!)),
                  ),
                ),
                SizedBox(height: 32),
                TextField(
                  controller: _clientController,
                  decoration: InputDecoration(
                    labelText: 'Client Name',
                    labelStyle: TextStyle(color: Colors.grey[600]),
                    border: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                    enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                    focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.orange[600]!)),
                  ),
                ),
                SizedBox(height: 32),
                TextField(
                  controller: _invoiceController,
                  decoration: InputDecoration(
                    labelText: 'Invoice Number',
                    labelStyle: TextStyle(color: Colors.grey[600]),
                    border: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                    enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                    focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.orange[600]!)),
                  ),
                ),
                SizedBox(height: 32),
                Row(
                  children: [
                    Expanded(
                      child: Text(
                        'Date: ${_selectedDate.day}/${_selectedDate.month}/${_selectedDate.year}',
                        style: TextStyle(fontSize: 16, color: Colors.grey[800]),
                      ),
                    ),
                    IconButton(
                      icon: Icon(Icons.calendar_today_outlined, color: Colors.orange[600], size: 20),
                      onPressed: () async {
                        final date = await showDatePicker(
                          context: context,
                          initialDate: _selectedDate,
                          firstDate: DateTime(2020),
                          lastDate: DateTime(2030),
                        );
                        if (date != null) setState(() => _selectedDate = date);
                      },
                    ),
                  ],
                ),
                SizedBox(height: 32),
                TextField(
                  controller: _descriptionController,
                  maxLines: 3,
                  decoration: InputDecoration(
                    labelText: 'Description',
                    labelStyle: TextStyle(color: Colors.grey[600]),
                    border: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                    enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                    focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.orange[600]!)),
                  ),
                ),
                SizedBox(height: 48),
                Text(
