                    Expanded(
                      flex: 2,
                      child: TextField(
                        controller: _itemController,
                        decoration: InputDecoration(
                          labelText: 'Item Name',
                          labelStyle: TextStyle(color: Colors.grey[600]),
                          border: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                          enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                          focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.orange[600]!)),
                        ),
                      ),
                    ),
                    SizedBox(width: 16),
                    Expanded(
                      child: TextField(
                        controller: _quantityController,
                        keyboardType: TextInputType.number,
                        decoration: InputDecoration(
                          labelText: 'Qty',
                          labelStyle: TextStyle(color: Colors.grey[600]),
                          border: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                          enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                          focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.orange[600]!)),
                        ),
                      ),
                    ),
                    SizedBox(width: 16),
                    Expanded(
                      child: TextField(
                        controller: _priceController,
                        keyboardType: TextInputType.numberWithOptions(decimal: true),
                        decoration: InputDecoration(
                          labelText: 'Price',
                          labelStyle: TextStyle(color: Colors.grey[600]),
                          border: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                          enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                          focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.orange[600]!)),
                        ),
                      ),
                    ),
                  ],
                ),
                SizedBox(height: 40),
                Container(
                  padding: EdgeInsets.symmetric(vertical: 16, horizontal: 20),
                  decoration: BoxDecoration(
                    border: Border.all(color: Colors.orange[200]!, width: 1),
                    borderRadius: BorderRadius.circular(4),
                  ),
                  child: Text(
                    'Total: $${_total.toStringAsFixed(2)}',
                    style: TextStyle(
                      fontSize: 18,
                      fontWeight: FontWeight.w500,
                      color: Colors.orange[600],
                    ),
                    textAlign: TextAlign.center,
                  ),
                ),
                SizedBox(height: 48),
                OutlinedButton(
                  onPressed: () {},
                  style: OutlinedButton.styleFrom(
                    side: BorderSide(color: Colors.orange[600]!, width: 1.5),
                    padding: EdgeInsets.symmetric(vertical: 16),
                    shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(4)),
                  ),
                  child: Text(
                    'Generate Invoice',
                    style: TextStyle(
                      fontSize: 16,
                      color: Colors.orange[600],
                      fontWeight: FontWeight.w500,
                    ),
                  ),
                ),
              ],
            ),
          ),
        ),
      ),
    );
  }
}
