import 'package:flutter/material.dart';

class GeneratedWidget extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.yellow[50],
      appBar: AppBar(
        backgroundColor: Colors.yellow[50],
        elevation: 0,
        title: Text('Overview', style: TextStyle(color: Colors.black87, fontWeight: FontWeight.w600)),
        actions: [
          Icon(Icons.search, color: Colors.black87),
          SizedBox(width: 16),
        ],
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(16),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            Text('Hi, Mia!', style: TextStyle(fontSize: 24, fontWeight: FontWeight.bold, color: Colors.black87)),
            SizedBox(height: 20),
            GridView.count(
              crossAxisCount: 2,
              shrinkWrap: true,
              physics: NeverScrollableScrollPhysics(),
              crossAxisSpacing: 16,
              mainAxisSpacing: 16,
              children: [
                _buildMetricCard('12,345', 'Orders', Colors.pink[100]!, Colors.pink[800]!, Icons.trending_up),
                _buildMetricCard('9,012', 'Users', Colors.teal[100]!, Colors.teal[800]!, Icons.person_add),
                _buildMetricCard('96%', 'Satisfaction', Colors.orange[100]!, Colors.orange[800]!, Icons.star),
                _buildMetricCard('\$60K', 'Revenue', Colors.purple[100]!, Colors.purple[800]!, Icons.monetization_on),
              ],
            ),
            SizedBox(height: 24),
            Text('Recent Activity', style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600, color: Colors.black87)),
            SizedBox(height: 12),
            _buildActivityItem(Icons.shopping_cart, 'Order Placed', 'Order #1234', '1 min ago', Colors.pink[800]!),
            _buildActivityItem(Icons.person, 'User Joined', 'Mia Johnson joined', '4 min ago', Colors.teal[800]!),
            _buildActivityItem(Icons.payment, 'Payment', '\$4,999.99', '8 min ago', Colors.purple[800]!),
          ],
        ),
      ),
    );
  }

  Widget _buildMetricCard(String value, String label, Color bgColor, Color iconColor, IconData icon) {
    return Container(
      padding: EdgeInsets.all(16),
      decoration: BoxDecoration(
        color: bgColor,
        borderRadius: BorderRadius.circular(12),
        boxShadow: [BoxShadow(color: Colors.grey[300]!, blurRadius: 8, offset: Offset(0, 2))],
      ),
      child: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          Row(
            mainAxisAlignment: MainAxisAlignment.spaceBetween,
            children: [
              Icon(icon, color: iconColor, size: 24),
              Icon(Icons.more_vert, color: Colors.grey[600], size: 16),
            ],
          ),
          Spacer(),
          Text(value, style: TextStyle(fontSize: 20, fontWeight: FontWeight.bold, color: Colors.black87)),
          Text(label, style: TextStyle(fontSize: 12, color: Colors.grey[600])),
        ],
      ),
    );
  }

  Widget _buildActivityItem(IconData icon, String title, String subtitle, String time, Color borderColor) {
    return Container(
      margin: EdgeInsets.only(bottom: 12),
      padding: EdgeInsets.all(12),
      decoration: BoxDecoration(
        color: Colors.white,
        borderRadius: BorderRadius.circular(8),
        border: Border.all(color: borderColor),
      ),
      child: Row(
        children: [
          CircleAvatar(
            radius: 20,
            backgroundColor: borderColor.withOpacity(0.2),
            child: Icon(icon, color: borderColor, size: 20),
          ),
          SizedBox(width: 12),
          Expanded(
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                Text(title, style: TextStyle(fontWeight: FontWeight.w600, fontSize: 14, color: Colors.black87)),
                Text(subtitle, style: TextStyle(color: Colors.grey[600], fontSize: 12)),
              ],
            ),
          ),
          Text(time, style: TextStyle(color: Colors.grey[500], fontSize: 11)),
        ],
      ),
    );
  }
}
