import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> with SingleTickerProviderStateMixin {
  bool _notificationsEnabled = true;
  bool _darkModeEnabled = false;
  late TabController _tabController;

  @override
  void initState() {
    super.initState();
    _tabController = TabController(length: 3, vsync: this);
  }

  @override
  void dispose() {
    _tabController.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Color(0xFFECEFF1),
      appBar: AppBar(
        title: Text('Settings', style: TextStyle(color: Colors.black, fontWeight: FontWeight.w600)),
        backgroundColor: Color(0xFFECEFF1),
        elevation: 0,
        bottom: TabBar(
          controller: _tabController,
          labelColor: Color(0xFF546E7A),
          unselectedLabelColor: Colors.grey,
          indicatorColor: Color(0xFF546E7A),
          tabs: [
            Tab(text: 'Account'),
            Tab(text: 'Preferences'),
            Tab(text: 'Support'),
          ],
        ),
      ),
      body: Column(
        children: [
          Card(
            elevation: 2,
            margin: EdgeInsets.all(16),
            shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
            child: Padding(
              padding: EdgeInsets.all(16),
              child: Row(
                children: [
                  CircleAvatar(
                    radius: 40,
                    backgroundColor: Color(0xFF546E7A),
                    child: Icon(Icons.person, size: 50, color: Colors.white),
                  ),
                  SizedBox(width: 16),
                  Column(
                    crossAxisAlignment: CrossAxisAlignment.start,
                    children: [
                      Text('Jane Smith', style: TextStyle(fontSize: 18, fontWeight: FontWeight.w600)),
                      Text('jane.smith@email.com', style: TextStyle(fontSize: 14, color: Colors.grey[600])),
                    ],
                  ),
                ],
              ),
            ),
          ),
          Expanded(
            child: TabBarView(
              controller: _tabController,
              children: [
                _buildTabContent([
                  _buildListTile(Icons.edit, 'Edit Profile', trailing: Icon(Icons.chevron_right, color: Color(0xFF546E7A))),
                  _buildListTile(Icons.lock, 'Change Password', trailing: Icon(Icons.chevron_right, color: Color(0xFF546E7A))),
                  _buildListTile(Icons.privacy_tip, 'Privacy', trailing: Icon(Icons.chevron_right, color: Color(0xFF546E7A))),
                ]),
                _buildTabContent([
                  _buildListTile(Icons.notifications, 'Notifications', trailing: Switch(
                    value: _notificationsEnabled,
                    onChanged: (val) => setState(() => _notificationsEnabled = val),
                    activeColor: Color(0xFF546E7A),
                  )),
                  _buildListTile(Icons.dark_mode, 'Dark Mode', trailing: Switch(
                    value: _darkModeEnabled,
                    onChanged: (val) => setState(() => _darkModeEnabled = val),
                    activeColor: Color(0xFF546E7A),
                  )),
                  _buildListTile(Icons.language, 'Language', trailing: Icon(Icons.chevron_right, color: Color(0xFF546E7A))),
                ]),
                _buildTabContent([
                  _buildListTile(Icons.help, 'Help', trailing: Icon(Icons.chevron_right, color: Color(0xFF546E7A))),
                  _buildListTile(Icons.contact_support, 'Contact', trailing: Icon(Icons.chevron_right, color: Color(0xFF546E7A))),
                  _buildListTile(Icons.info, 'About', trailing: Icon(Icons.chevron_right, color: Color(0xFF546E7A))),
                ]),
              ],
            ),
          ),
          Padding(
            padding: EdgeInsets.all(16),
            child: ElevatedButton(
              onPressed: () {},
              style: ElevatedButton.styleFrom(
                backgroundColor: Colors.red,
                foregroundColor: Colors.white,
                padding: EdgeInsets.symmetric(vertical: 16),
                shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
                minimumSize: Size(double.infinity, 50),
              ),
              child: Text('Logout', style: TextStyle(fontSize: 16, fontWeight: FontWeight.w600)),
            ),
          ),
        ],
      ),
    );
  }

  Widget _buildListTile(IconData icon, String title, {Widget? trailing}) {
    return Card(
      elevation: 1,
      shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(8)),
      margin: EdgeInsets.symmetric(horizontal: 16, vertical: 4),
      child: ListTile(
        leading: Icon(icon, color: Color(0xFF546E7A)),
        title: Text(title, style: TextStyle(fontSize: 16)),
        trailing: trailing,
        onTap: () {},
      ),
    );
  }

  Widget _buildTabContent(List<Widget> children) {
    return SingleChildScrollView(
      child: Column(
        children: children,
      ),
    );
  }
}
