import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  final _nameController = TextEditingController();
  final _emailController = TextEditingController();
  final _feedbackController = TextEditingController();
  String _feedbackType = '';
  int _emojiRating = 0;
  final List<String> _types = ['Positive', 'Neutral', 'Negative'];
  final List<String> _emojis = ['😞', '😐', '😊', '😍', '🤩'];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.white,
      body: SafeArea(
        child: SingleChildScrollView(
          padding: EdgeInsets.symmetric(horizontal: 32, vertical: 48),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              Text(
                'Feedback',
                style: TextStyle(
                  fontSize: 36,
                  fontWeight: FontWeight.w300,
                  color: Colors.black87,
                  letterSpacing: -1,
                ),
              ),
              SizedBox(height: 48),
              TextField(
                controller: _nameController,
                style: TextStyle(fontSize: 16, color: Colors.black87),
                decoration: InputDecoration(
                  labelText: 'Name',
                  labelStyle: TextStyle(color: Colors.grey[600], fontSize: 14),
                  enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                  focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.green[600]!, width: 2)),
                  border: InputBorder.none,
                ),
              ),
              SizedBox(height: 32),
              TextField(
                controller: _emailController,
                style: TextStyle(fontSize: 16, color: Colors.black87),
                decoration: InputDecoration(
                  labelText: 'Email',
                  labelStyle: TextStyle(color: Colors.grey[600], fontSize: 14),
                  enabledBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.grey[300]!)),
                  focusedBorder: UnderlineInputBorder(borderSide: BorderSide(color: Colors.green[600]!, width: 2)),
                  border: InputBorder.none,
                ),
              ),
              SizedBox(height: 40),
              Text(
                'Type of feedback',
                style: TextStyle(fontSize: 14, color: Colors.grey[700], fontWeight: FontWeight.w500),
              ),
              SizedBox(height: 16),
              Column(
                children: _types.map((type) => RadioListTile<String>(
                  title: Text(type, style: TextStyle(fontSize: 16, color: Colors.black87)),
                  value: type,
                  groupValue: _feedbackType,
                  activeColor: Colors.green[600],
                  onChanged: (value) => setState(() => _feedbackType = value!),
                  contentPadding: EdgeInsets.zero,
                )).toList(),
              ),
              SizedBox(height: 32),
              Text(
                'How do you feel?',
                style: TextStyle(fontSize: 14, color: Colors.grey[700], fontWeight: FontWeight.w500),
              ),
              SizedBox(height: 16),
              Row(
                mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                children: List.generate(5, (index) => GestureDetector(
                  onTap: () => setState(() => _emojiRating = index + 1),
                  child: Container(
                    padding: EdgeInsets.all(8),
                    decoration: BoxDecoration(
                      color: _emojiRating == index + 1 ? Colors.green[50] : Colors.transparent,
                      borderRadius: BorderRadius.circular(8),
                      border: _emojiRating == index + 1 ? Border.all(color: Colors.green[300]!) : null,
                    ),
                    child: Text(
                      _emojis[index],
                      style: TextStyle(fontSize: 32),
                    ),
                  ),
                )),
              ),
              SizedBox(height: 40),
              TextField(
                controller: _feedbackController,
                style: TextStyle(fontSize: 16, color: Colors.black87),
                maxLines: 6,
                decoration: InputDecoration(
                  labelText: 'Your message',
                  labelStyle: TextStyle(color: Colors.grey[600], fontSize: 14),
                  enabledBorder: OutlineInputBorder(
                    borderSide: BorderSide(color: Colors.grey[300]!, width: 1),
                    borderRadius: BorderRadius.circular(4),
                  ),
                  focusedBorder: OutlineInputBorder(
                    borderSide: BorderSide(color: Colors.green[600]!, width: 2),
                    borderRadius: BorderRadius.circular(4),
                  ),
                ),
              ),
              SizedBox(height: 48),
              SizedBox(
                width: double.infinity,
                child: TextButton(
                  onPressed: () {},
                  style: TextButton.styleFrom(
                    backgroundColor: Colors.green[600],
                    foregroundColor: Colors.white,
                    padding: EdgeInsets.symmetric(vertical: 16),
                    shape: RoundedRectangleBorder(borderRadius: BorderRadius.circular(4)),
                  ),
                  child: Text(
                    'Send Feedback',
                    style: TextStyle(fontSize: 16, fontWeight: FontWeight.w500, letterSpacing: 0.5),
                  ),
                ),
              ),
            ],
          ),
        ),
      ),
    );
  }
}
