import 'package:flutter/material.dart';

class GeneratedWidget extends StatefulWidget {
  @override
  _GeneratedWidgetState createState() => _GeneratedWidgetState();
}

class _GeneratedWidgetState extends State<GeneratedWidget> {
  final _formKey = GlobalKey<FormState>();
  final _nameController = TextEditingController();
  final _emailController = TextEditingController();
  final _feedbackController = TextEditingController();
  String? _selectedCategory;
  int _rating = 0;
  final List<String> _categories = ['Bug', 'Feature', 'General', 'Support'];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      backgroundColor: Colors.black,
      appBar: AppBar(
        backgroundColor: Colors.black,
        elevation: 0,
        title: Text('Feedback Portal', style: TextStyle(fontSize: 26, fontFamily: 'Orbitron', fontWeight: FontWeight.bold, color: Colors.greenAccent)),
        centerTitle: true,
      ),
      body: SingleChildScrollView(
        padding: EdgeInsets.all(16),
        child: Form(
          key: _formKey,
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.stretch,
            children: [
              Text(
                'Transmit your thoughts to our system',
                style: TextStyle(fontSize: 14, color: Colors.greenAccent.withOpacity(0.7)),
                textAlign: TextAlign.center,
              ),
              SizedBox(height: 20),
              TextFormField(
                controller: _nameController,
                decoration: InputDecoration(
                  labelText: 'Name',
                  labelStyle: TextStyle(color: Colors.greenAccent),
                  filled: true,
                  fillColor: Color(0xFF121212),
                  border: OutlineInputBorder(borderRadius: BorderRadius.zero, borderSide: BorderSide(color: Colors.greenAccent, width: 2)),
                  focusedBorder: OutlineInputBorder(
                    borderRadius: BorderRadius.zero,
                    borderSide: BorderSide(color: Colors.greenAccent, width: 3),
                  ),
                ),
                style: TextStyle(color: Colors.greenAccent),
              ),
              SizedBox(height: 16),
              TextFormField(
                controller: _emailController,
                decoration: InputDecoration(
                  labelText: 'Email',
                  labelStyle: TextStyle(color: Colors.greenAccent),
                  filled: true,
                  fillColor: Color(0xFF121212),
                  border: OutlineInputBorder(borderRadius: BorderRadius.zero, borderSide: BorderSide(color: Colors.greenAccent, width: 2)),
                  focusedBorder: OutlineInputBorder(
                    borderRadius: BorderRadius.zero,
                    borderSide: BorderSide(color: Colors.greenAccent, width: 3),
                  ),
                ),
                keyboardType: TextInputType.emailAddress,
                style: TextStyle(color: Colors.greenAccent),
              ),
              SizedBox(height: 16),
              DropdownButtonFormField<String>(
                decoration: InputDecoration(
                  labelText: 'Feedback Type',
                  labelStyle: TextStyle(color: Colors.greenAccent),
                  filled: true,
                  fillColor: Color(0xFF121212),
                  border: OutlineInputBorder(borderRadius: BorderRadius.zero, borderSide: BorderSide(color: Colors.greenAccent, width: 2)),
                  focusedBorder: OutlineInputBorder(
                    borderRadius: BorderRadius.zero,
                    borderSide: BorderSide(color: Colors.greenAccent, width: 3),
                  ),
                ),
                value: _selectedCategory,
                items: _categories.map((category) => DropdownMenuItem(
                  value: category,
                  child: Text(category, style: TextStyle(color: Colors.greenAccent)),
                )).toList(),
                onChanged: (value) => setState(() => _selectedCategory = value),
                style: TextStyle(color: Colors.greenAccent),
              ),
              SizedBox(height: 16),
              Text('Rate Us:', style: TextStyle(fontSize: 16, color: Colors.greenAccent, fontWeight: FontWeight.w600)),
              SizedBox(height: 8),
              Row(
                mainAxisAlignment: MainAxisAlignment.center,
                children: List.generate(5, (index) => GestureDetector(
                  onTap: () => setState(() => _rating = index + 1),
                  child: Icon(
                    Icons.star,
                    size: 32,
                    color: index < _rating ? Colors.greenAccent : Colors.grey[800],
                  ),
                )),
              ),
              SizedBox(height: 16),
              TextFormField(
                controller: _feedbackController,
                decoration: InputDecoration(
                  labelText: 'Comments',
                  labelStyle: TextStyle(color: Colors.greenAccent),
                  filled: true,
                  fillColor: Color(0xFF121212),
                  border: OutlineInputBorder(borderRadius: BorderRadius.zero, borderSide: BorderSide(color: Colors.greenAccent, width: 2)),
                  focusedBorder: OutlineInputBorder(
                    borderRadius: BorderRadius.zero,
                    borderSide: BorderSide(color: Colors.greenAccent, width: 3),
                  ),
                ),
                maxLines: 4,
                style: TextStyle(color: Colors.greenAccent),
              ),
              SizedBox(height: 20),
              ElevatedButton(
                onPressed: () {},
                style: ElevatedButton.styleFrom(
                  backgroundColor: Colors.greenAccent,
                  foregroundColor: Colors.black,
                  padding: EdgeInsets.symmetric(vertical: 14),
                  shape: RoundedRectangleBorder(borderRadius: BorderRadius.zero),
                  elevation: 5,
                  shadowColor: Colors.greenAccent.withOpacity(0.5),
                ),
                child: Text('Transmit Feedback', style: TextStyle(fontSize: 16, fontWeight: FontWeight.bold)),
              ),
            ],
          ),
        ),
      ),
    );
  }
}
